        <Route exact path="/admin/revenue" element={<AdminRevenue />} />
        <Route exact path="/admin/fabrics" element={<AdminFabrics />} />
        <Route
          exact
          path="/admin/invoice-data"
          element={<AdminInvoiceData />}
        />
        <Route
          exact
          path="/admin/service-times"
          element={<AdminServiceTimes />}
        />
        <Route exact path="/admin/tailors" element={<AdminTailors />} />
        <Route exact path="/admin/users/:id" element={<AdminTailorDetails />} />
        <Route exact path="/admin/products" element={<AdminProducts />} />
        <Route
          exact
          path="/admin/products/:id"
          element={<AdminProductsView />}
        />
        {/* Users Routes */}
        <Route exact path="/users/login" element={<Login />} />
        <Route
          exact
          path="/users/forgot-password"
          element={<ForgotPassword />}
        />
        <Route exact path="/users/reset-password" element={<ResetPassword />} />
        <Route exact path="/users/register-user" element={<RegisterUser />} />
        <Route exact path="/users/create-master" element={<CreateMaster />} />
        <Route exact path="/users/edit-user/:id" element={<EditUser />} />
        {/* Misc Routes */}
        <Route exact path="/unauthorized" element={<NotFound />} />
        <Route exact path="*" element={<NotFound />} />
      </Routes>
    </Suspense>
  );
}

export default App;
