import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { applyAnimation } from "../../functions";

const MyOrders = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [duties, setDuties] = useState([]);

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/orders/my-orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setOrders(response.data.orders);
            setIsLoading(false);
            setUser(response.data.user);
            document.title = `Order Management - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, navigate, serverUrl]);

  const determineDuty = (order) => {
    const activeUser = `${user.name} ${user.surname}`;
    let roles = [];
    if (activeUser === order.measurer) {
      setDuties(...duties, "Measuring");
    }

    if (activeUser === order.sewer) {
      setDuties(...duties, "Sewing");
    }

    if (activeUser === order.cutter) {
      setDuties(...duties, "Cutting");
    }

    // return roles.join(", ");
    // return duties;
  };

  return (
    <>
      <Navbar />
      <main>
        <section className="py-0 px-[2rem] justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="utilities">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> Back to Orders
            </Link>
          </div>
          {isLoading && (
            <table className="text-center">
              <thead>
                <tr>
                  <td>Loading...</td>
                </tr>
              </thead>
              <tbody>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
              </tbody>
            </table>
          )}
          <table>
            <caption>My Orders</caption>
            <thead>
              <tr>
                <td>Order ID</td>
                <td>Date</td>
                <td>Status</td>
                <td>Duty</td>
                <td></td>
              </tr>
            </thead>
            <tbody>
              {orders &&
                orders.map((order, index) => (
                  <tr key={index}>
                    <td>{order._id}</td>
                    <td>
                      {new Date(order.order_date).toLocaleDateString("en-us", {
                        month: "long",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </td>
                    <td>
                      <span
                        className={`${applyAnimation(
                          order.order_status
                        )} p-[0.2rem] rounded-[0.7rem]`}
                      >
                        {order.order_status}
                      </span>
                    </td>
                    <td className="flex flex-col">{determineDuty(order)}</td>
                    <td>
                      <Link to={`/orders/${order._id}`} target="_blank">
                        Details <i className="fas fa-external-link"></i>
                      </Link>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
          <Footer />
        </section>
      </main>
    </>
  );
};

export default MyOrders;
