import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import OrderForm from "./OrderForm";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const NewOrder = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [order, setOrder] = useState([]);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState({});
  const [orderName, setOrderName] = useState("");
  const [dependentName, setDependentName] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/orders`
    : `https://khamcotailor.com/orders`;
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "New Order - Khamco Shop";

    const fetchOrderDetails = async () => {
      await axios
        .get(`${apiUrl}/create/new-order/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setOrder(response.data.order);
          setCustomers(response.data.customers);
          setProducts(response.data.products);
          setServices(response.data.services);
        })
        .catch((error) => {
          toast.error(error.message);
          console.error(error.message);
        });
    };

    fetchOrderDetails();
  }, [apiUrl, authKey, id]);

  const createOrderName = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (!orderName) {
      toast.warn("Please select a value");
    }
    await axios
      .put(
        `${apiUrl}/${id}/name`,
        {
          order_name: orderName,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        setOrder(response.data.order);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        toast.error(error.message);
        console.error(error.message);
      });
  };

  const deleteOrderName = async (e, orderId, nameId) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/${orderId}/names/${nameId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setOrder(response.data.order);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const addDependent = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (!dependentName) {
      toast.warn("Please select a value");
    }
    await axios.put(
      `${apiUrl}/${id}/customers`,
      {
        dependent_name: dependentName,
        customer_name: customerName,
        customer_surname: customerSurname,
      },
      {
        headers: { Authorization: `Bearer ${authKey}` },
      }
    );
  };

  const handleChange = (e) => {
    const selectedOption = e.target.options[e.target.selectedIndex];
    setCustomerSurname(selectedOption.dataset.surname);
    setCustomerName(selectedOption.value);
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> Orders
            </Link>
          </div>
          <h1 className="title">New Order</h1>
          <div className="border-t-2 border-b-2 border-black my-[2rem]">
            {order.name && order.name.length > 0 ? (
              order.name.map((item, index) => (
                <form className="flex flex-col justify-center items-center w-[90vw]">
                  <div className="form-row">
                    <div className="flex gap-[1rem] w-full justify-start flex-wrap items-center">
                      <label
                        className="border-b-[0.1rem] border-[gray] p-b-[0.5rem] w-[85%]"
                        key={index}
                      >
                        {item.choice}
                      </label>
                      <button
                        type="button"
                        className="delete-btn width-[10%]"
                        title="Remove this item"
                        onClick={(e) => deleteOrderName(e, order._id, item._id)}
                        disabled={loading}
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <i className="fas fa-minus"></i>
                        )}
                      </button>
                    </div>
                  </div>
                </form>
              ))
            ) : (
              <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                Nothing here yet...
              </p>
            )}
          </div>
          <form className="flex flex-col justify-center items-center w-[90vw]">
            <div className="form-row">
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select
                  className="w-[85%]"
                  onChange={(e) => setOrderName(e.target.value)}
                >
                  <option label="-- Products --" disabled></option>
                  {products.map((product, index) => (
                    <option
                      key={index}
                      value={product.name}
                      label={product.name}
                    ></option>
                  ))}
                  <option label="-- Services --" disabled></option>
                  {services.map((service, index) => (
                    <option
                      key={index}
                      value={service.service}
                      label={service.service}
                    ></option>
                  ))}
                </select>
                <button
                  onClick={createOrderName}
                  className="edit-btn"
                  title="Add another item"
                  disabled={loading}
                >
                  {loading ? (
                    <i className="fas fa-spinner animate-spin"></i>
                  ) : (
                    <i className="fas fa-plus"></i>
                  )}
                </button>
              </div>
            </div>
          </form>
          <div className="form-row">
            <h1 className="text-center text-2xl">Customer Details</h1>
          </div>
          {order.customers &&
            order.customers.map((customer, index) => (
              <form
                action="/orders/<%= order.id %>/customers/<%= customer.id %>?_method=DELETE"
                method="POST"
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <div className="form-row">
                  <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                    <label
                      className="border-b-[0.1rem] border-b-gray-400 pb-[0.5rem] w-[85%]"
                      key={index}
                    >
                      {customer.customer_name}
                    </label>
                    <button
                      type="button"
                      className="delete-btn w-[10%]"
                      title="Remove this item"
                    >
                      <i className="fas fa-minus"></i>
                    </button>
                  </div>
                </div>
              </form>
            ))}
          <form
            action="/orders/<%= order.id %>/customers?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="form-row">
              <select onChange={handleChange}>
                <option
                  label="-- Select customer --"
                  defaultChecked
                  disabled
                ></option>
                {customers.map((customer, index) => (
                  <option
                    key={index}
                    value={customer.name}
                    label={customer.name}
                    data-surname={customer.surname}
                  ></option>
                ))}
              </select>
            </div>
            <div className="form-row">
              <label htmlFor="dependents">Dependent Name</label>
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select
                  className="w-[85%]"
                  onChange={(e) => setDependentName(e.target.value)}
                >
                  {selectedCustomer.dependents &&
                    selectedCustomer.dependents.map((customer, index) => (
                      <option key={index}>{customer.name}</option>
                    ))}
                </select>
                <button
                  type="button"
                  className="edit-btn w-[10%]"
                  title="Add another customer"
                  onClick={addDependent}
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
          </form>
          <form
            action="/orders/<%= order.id%>?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <OrderForm customerSurname={customerSurname} orderId={id} />
          </form>
          <form
            action="/orders/<%= order.id%>?_method=DELETE"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <button type="button" className="delete-btn">
              Delete <i className="fas fa-times"></i>
            </button>
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewOrder;
