import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import Footer from "../../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";
import MeasurementsForm from "./MeasurementsForm";
import { areaNames } from "../../../utilities";

const CreateMeasurement = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/create-measurement/${id}`
    : `https://khamco-shop-backend.onrender.com/customers/create-measurement/${id}`;
  const authKey = localStorage.getItem("jwt");
  const [clothingOptions, setClothingOptions] = useState([]);
  const [takenBy, setTakenBy] = useState("");
  const [description, setDescription] = useState("");
  const [dishdashaLength, setDishdashaLength] = useState(0);
  const [dishdashaFrontHeight, setDishdashaFrontHeight] = useState(0);
  const [dishdashaBackHeight, setDishdashaBackHeight] = useState(0);
  const [shirtType, setShirtType] = useState("");
  const [garmentLength, setGarmentLength] = useState(0);
  const [pantsLength, setPantsLength] = useState(0);
  const [shortsLength, setShortsLength] = useState(0);
  const [dressLength, setDressLength] = useState(0);
  const [collar, setCollar] = useState(0);
  const [shoulders, setShoulders] = useState(0);
  const [sleeve, setSleeve] = useState(0);
  const [shirtSleeve, setShirtSleeve] = useState(0);
  const [dressSleeve, setDressSleeve] = useState(0);
  const [sleepwearSleeve, setSleepwearSleeve] = useState(0);
  const [chest, setChest] = useState(0);
  const [belly, setBelly] = useState(0);
  const [waist, setWaist] = useState(0);
  const [hips, setHips] = useState(0);
  const [armHole, setArmHole] = useState(0);
  const [bicep_tricep, setBicepTricep] = useState(0);
  const [elbow, setElbow] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [inseam, setInseam] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [calf, setCalf] = useState(0);
  const [leg_foot, setLegFoot] = useState(0);
  const [backWidth, setBackWidth] = useState(0);
  const [backHeight, setBackHeight] = useState(0);
  const [frontChestWidth, setFrontChestWidth] = useState(0);
  const [specialOrder, setSpecialOrder] = useState("");
  const [alterations, setAlterations] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const [date, setDate] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setAuthorizedUser(
            `${response.data.authorizedUser.name} ${response.data.authorizedUser.surname}`
          );
          document.title = `Add Measurements for ${response.data.customer.name} ${response.data.customer.surname}`;
        })
        .catch((error) => {
          toast.error(error.response?.data?.message || error.message);
          setCustomer(null);
        });
    };

    return () => {
      fetchData();
    };
  }, [apiUrl, authKey]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    if (!date) {
      toast.error("Date is required.");
      return;
    }

    if (!takenBy) {
      toast.error("Please enter the measurers name");
      return;
    }

    try {
      const response = await axios.post(
        apiUrl,
        {
          date_created: date,
          clothing_options: clothingOptions,
          taken_by: takenBy,
          dishdasha_garment_length: dishdashaLength,
          dishdasha_front_height: dishdashaFrontHeight,
          dishdasha_back_height: dishdashaBackHeight,
          shirt_type: shirtType,
          garment_length: garmentLength,
          pants_garment_length: pantsLength,
          shorts_garment_length: shortsLength,
          dress_garment_length: dressLength,
          sleeve: sleeve,
          shirt_sleeve: shirtSleeve,
          dress_sleeve: dressSleeve,
          sleepwear_sleeve: sleepwearSleeve,
          chest: chest,
          collar: collar,
          shoulders: shoulders,
          belly: belly,
          waist: waist,
          hips: hips,
          arm_hole: armHole,
          bicep_tricep: bicep_tricep,
          elbow: elbow,
          wrist: wrist,
          inseam: inseam,
          thighs: thigh,
          calf: calf,
          leg_foot: leg_foot,
          back_width: backWidth,
          back_height: backHeight,
          front_chest_width: frontChestWidth,
          special_order: specialOrder,
          alterations: alterations,
          description: description,
          notes: notes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );

      toast.success(response.data.message);
      setCustomer(response.data.customer);
      navigate(`/customers/${response.data.customer._id}/measurements`);
      setLoading(false);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
      setCustomer(null);
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="p-[4rem] grid justify-center items-center">
          {customer && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}/measurements`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> Measurements
                </Link>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">
                {`New Measurement for ${customer.name} ${customer.surname}`}
              </h1>
            </>
          )}
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">
                Date
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="date"
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </div>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">
                Measurer
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="text"
                placeholder="Measurements taken by"
                onChange={(e) => {
                  setTakenBy(e.target.value);
                }}
              />
            </div>
            <MeasurementsForm
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              setDishdashaLength={setDishdashaLength}
              setDishdashaFrontHeight={setDishdashaFrontHeight}
              setDishdashaBackHeight={setDishdashaBackHeight}
              shirtType={shirtType}
              setShirtType={setShirtType}
              setGarmentLength={setGarmentLength}
              setPantsLength={setPantsLength}
              setShortsLength={setShortsLength}
              setDressLength={setDressLength}
              setCollar={setCollar}
              setShoulders={setShoulders}
              setSleeve={setSleeve}
              setShirtSleeve={setShirtSleeve}
              setDressSleeve={setDressSleeve}
              setDescription={setDescription}
              setSleepwearSleeve={setSleepwearSleeve}
              setChest={setChest}
              setBelly={setBelly}
              setWaist={setWaist}
              setHips={setHips}
              setArmHole={setArmHole}
              setBicepTricep={setBicepTricep}
              loading={loading}
              areas={areaNames}
              setElbow={setElbow}
              setWrist={setWrist}
              setInseam={setInseam}
              setThigh={setThigh}
              setCalf={setCalf}
              setLegFoot={setLegFoot}
              setBackWidth={setBackWidth}
              setBackHeight={setBackHeight}
              setFrontChestWidth={setFrontChestWidth}
              setSpecialOrder={setSpecialOrder}
              setAlterations={setAlterations}
              setNotes={setNotes}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateMeasurement;
