import { useCallback, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { useStoreState } from "easy-peasy";

const Home = ({ user }) => {
  const [message, setMessage] = useState("");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  const isTokenExpired = useCallback((token) => {
    const payloadBase64 = token.split(".")[1];
    const decodedJson = atob(payloadBase64);
    const decoded = JSON.parse(decodedJson);
    const exp = decoded.exp;
    const now = Date.now().valueOf() / 1000;
    return now > exp;
  }, []);

  useEffect(() => {
    document.title = "Khamco Shop Home";

    // const isLoggedIn = () => {
    //   const token = authKey;
    //   return token != null && !isTokenExpired(token);
    // };

    // if (!isLoggedIn()) {
    //   localStorage.clear();
    //   navigate("/users/login");
    // }

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
        console.log(writingDirection);
      }
    };

    return () => {
      determineWritingDirection();
    };
  }, [isTokenExpired, authKey, navigate, userLanguage, writingDirection]);

  return (
    <>
      <Navbar />
      <main className="p-4 w-[90%] m-auto font-minimoMedium">
        <h1 className="title">Main Menu</h1>
        <div className="flex justify-center gap-8 items-center my-8 mx-auto w-full flex-wrap">
          {message && <p className="message">{message}</p>}
        </div>
        <div
          className="flex justify-center items-center my-8 flex-wrap w-full"
          dir={writingDirection}
        >
          <div className="w-full bg-orange-300 grid grid-cols-1 md:grid-cols-3 gap-12 justify-center min-h-screen">
            <Link
              to="/customers"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-users"></i>
              <p>Customers</p>
            </Link>
            <Link
              to="/products"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-box-archive"></i>
              <p>Inventory</p>
            </Link>
            <Link
              to="/services"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-shirt"></i>
              <p>Services</p>
            </Link>
            <Link
              to="/invoices"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-receipt"></i>
              <p>Invoices</p>
            </Link>
            <Link
              to="/orders"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-bookmark"></i>
              <p>Orders</p>
            </Link>
            <Link
              to="/admin/dashboard"
              className="flex flex-col items-center justify-center gap-2 p-8 rounded-3xl bg-[#0000ffcc] text-white text-lg hover:bg-[#0000ff]"
            >
              <i className="fas fa-ban"></i>
              <p>Administration</p>
            </Link>
          </div>
        </div>
      </main>
      <Footer />
    </>
  );
};

export default Home;
