import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { apiUrl, authKey } from "../../utilities";

const AdminInvoiceData = () => {
  const [invoiceData, setInvoiceData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Admin Invoice Data - Khamco Shop";
    const fetchInvoiceData = async () => {
      await axios
        .get(`${apiUrl}/admin/invoice_data`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setInvoiceData(response.data.invoice_data);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          return;
        });
    };

    fetchInvoiceData();
  }, [apiUrl, authKey, navigate]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section className="admin-dashboard">
          {isLoading && (
            <>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
                <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              </div>
              <table className="text-center w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                <thead className="text-khamco-primary font-bold bg-[#ddd]">
                  <tr>
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {display && (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">
                Recent Invoice Data
              </caption>
              <thead className="bg-[#ddd] text-khamco-primary font-bold">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2">Customer</td>
                  <td className="text-center p-2">Date</td>
                  <td className="text-center p-2">Invoices Created</td>
                  <td className="text-center p-2">Invoices Deleted</td>
                </tr>
              </thead>
              <tbody>
                {invoiceData &&
                  invoiceData.map((entry, index) => (
                    <tr
                      key={index}
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                    >
                      <td className="text-center p-2">
                        {entry.customer_name} {entry.customer_surname}
                      </td>
                      <td className="text-center p-2">
                        {new Date(entry.date).toLocaleDateString("en-US", {
                          month: "long",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                      <td className="text-center p-2">
                        {entry.total_invoices_created}
                      </td>
                      <td className="text-center p-2">
                        {entry.total_invoices_deleted}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminInvoiceData;
