import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import CustomerForm from "../CustomerForm";
import { areaNames } from "../../../utilities";

const CreateDependent = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [defaultMembership, setDefaultMembership] = useState("Basic");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [militaryStatus, setMilitaryStatus] = useState("Civilian");
  const [membershipStatus, setMembershipStatus] = useState("Basic");
  const [militaryNumber, setMilitaryNumber] = useState("");
  const [militaryRank, setMilitaryRank] = useState("");
  const [militaryDivision, setMilitaryDivision] = useState("");
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [areas, setAreas] = useState(areaNames);
  // Measurement fields
  const [dishdashaLength, setDishdashaLength] = useState(0);
  const [dishdashaFrontHeight, setDishdashaFrontHeight] = useState(0);
  const [dishdashaBackHeight, setDishdashaBackHeight] = useState(0);
  const [shirtType, setShirtType] = useState("");
  const [garmentLength, setGarmentLength] = useState(0);
  const [pantsLength, setPantsLength] = useState(0);
  const [shortsLength, setShortsLength] = useState(0);
  const [dressLength, setDressLength] = useState(0);
  const [collar, setCollar] = useState(0);
  const [shoulders, setShoulders] = useState(0);
  const [sleeve, setSleeve] = useState(0);
  const [shirtSleeve, setShirtSleeve] = useState(0);
  const [dressSleeve, setDressSleeve] = useState(0);
  const [sleepwearSleeve, setSleepwearSleeve] = useState(0);
  const [chest, setChest] = useState(0);
  const [belly, setBelly] = useState(0);
  const [waist, setWaist] = useState(0);
  const [hips, setHips] = useState(0);
  const [armHole, setArmHole] = useState(0);
  const [bicep_tricep, setBicepTricep] = useState(0);
  const [elbow, setElbow] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [inseam, setInseam] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [calf, setCalf] = useState(0);
  const [leg_foot, setLegFoot] = useState(0);
  const [backWidth, setBackWidth] = useState(0);
  const [backHeight, setBackHeight] = useState(0);
  const [frontChestWidth, setFrontChestWidth] = useState(0);
  const [specialOrder, setSpecialOrder] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers`
    : `https://khamco-shop-backend.onrender.com/customers`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCustomer = async () => {
      await axios
        .get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          document.title = `Create a Dependent for ${response.data.customer.name}`;
        })
        .catch((error) => {
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          setCustomer(null);
        });
    };

    return () => {
      fetchCustomer();
    };
  }, [apiUrl, authKey, id]);

  const submitCustomer = async (event) => {
    event.preventDefault();
    setLoading(true);
    if (!customerName) {
      toast.error("Customer name is required");
      return;
    }
    try {
      const response = await axios.post(
        `${apiUrl}/create-dependent/${id}`,
        {
          name: customerName,
          surname: customerSurname,
          email: customerEmail,
          phone: customerPhone,
          military_status: militaryStatus,
          date_created: date,
          military_idnumber: militaryNumber,
          military_rank: militaryRank,
          military_department: militaryDivision,
          clothing_options: clothingOptions,
          dishdasha_garment_length: dishdashaLength,
          dishdasha_front_height: dishdashaFrontHeight,
          dishdasha_back_height: dishdashaBackHeight,
          shirt_type: shirtType,
          garment_length: garmentLength,
          pants_garment_length: pantsLength,
          shorts_garment_length: shortsLength,
          dress_garment_length: dressLength,
          sleeve: sleeve,
          shirt_sleeve: shirtSleeve,
          dress_sleeve: dressSleeve,
          sleepwear_sleeve: sleepwearSleeve,
          chest: chest,
          collar: collar,
          shoulders: shoulders,
          belly: belly,
          waist: waist,
          hips: hips,
          arm_hole: armHole,
          bicep_tricep: bicep_tricep,
          elbow: elbow,
          wrist: wrist,
          inseam: inseam,
          thighs: thigh,
          calf: calf,
          leg_foot: leg_foot,
          back_width: backWidth,
          back_height: backHeight,
          front_chest_width: frontChestWidth,
          special_order: specialOrder,
          description: description,
          notes: notes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      navigate(`/customers/${response.data.customer._id}`);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        {customer && (
          <>
            <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
              <Link
                to={`/customers/${customer._id}`}
                className="text-[blue] no-underline hover:underline cursor-pointer"
              >
                <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
                {customer.surname}
              </Link>
            </div>
            <h1 className="text-center text-[2rem] font-minimoBold">
              Add Dependent
            </h1>
            <section className="p-[4rem] grid justify-center items-center">
              <form
                onSubmit={submitCustomer}
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <CustomerForm
                  isCreateCustomer={true}
                  customer={customer}
                  membershipStatus={membershipStatus}
                  defaultMembership={defaultMembership}
                  setDefaultMembership={setDefaultMembership}
                  setCustomerName={setCustomerName}
                  setCustomerSurname={setCustomerSurname}
                  setCustomerEmail={setCustomerEmail}
                  setCustomerPhone={setCustomerPhone}
                  setHouseUnit={null}
                  setGovernorate={null}
                  setArea={null}
                  setCountry={null}
                  militaryStatus={militaryStatus}
                  setMilitaryStatus={setMilitaryStatus}
                  setMilitaryNumber={setMilitaryNumber}
                  setMilitaryRank={setMilitaryRank}
                  setMilitaryDivision={setMilitaryDivision}
                  setMembershipStatus={setMembershipStatus}
                  setDate={setDate}
                  clothingOptions={clothingOptions}
                  setClothingOptions={setClothingOptions}
                  setDishdashaLength={setDishdashaLength}
                  setDishdashaFrontHeight={setDishdashaFrontHeight}
                  setDishdashaBackHeight={setDishdashaBackHeight}
                  shirtType={shirtType}
                  setShirtType={setShirtType}
                  setGarmentLength={setGarmentLength}
                  setPantsLength={setPantsLength}
                  setShortsLength={setShortsLength}
                  setDressLength={setDressLength}
                  setCollar={setCollar}
                  setShoulders={setShoulders}
                  setSleeve={setSleeve}
                  setShirtSleeve={setShirtSleeve}
                  setDressSleeve={setDressSleeve}
                  setDescription={setDescription}
                  setSleepwearSleeve={setSleepwearSleeve}
                  setChest={setChest}
                  setBelly={setBelly}
                  setWaist={setWaist}
                  setHips={setHips}
                  setArmHole={setArmHole}
                  setBicepTricep={setBicepTricep}
                  loading={loading}
                  areas={areas}
                  setElbow={setElbow}
                  setWrist={setWrist}
                  setInseam={setInseam}
                  setThigh={setThigh}
                  setCalf={setCalf}
                  setLegFoot={setLegFoot}
                  setBackWidth={setBackWidth}
                  setBackHeight={setBackHeight}
                  setFrontChestWidth={setFrontChestWidth}
                  setSpecialOrder={setSpecialOrder}
                  setNotes={setNotes}
                  setAreas={setAreas}
                />
              </form>
            </section>
          </>
        )}
      </main>
      <Footer />
    </>
  );
};

export default CreateDependent;
