import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const ServicesView = () => {
  const { id } = useParams();
  const [service, setService] = useState({});
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/services"
    : "https://khamco-shop-backend.onrender.com/services";
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Service Details - Khamco Shop";

    const fetchService = async () => {
      try {
        const response = await axios.get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setService(response.data.service);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setService({});
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        } else {
          // Handle network errors or errors without a response object
          toast.error("An error occurred: " + error.message);
        }
      }
    };

    fetchService();
  }, [apiUrl, authKey, id, navigate]);

  const handleDelete = async (serviceId) => {
    try {
      const response = await axios.delete(`${apiUrl}/${serviceId}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/services");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link
              to="/services"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          {loading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(2).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-full bg-[#666] animate-pulse m-auto h-[100vh] my-[2rem]"></div>
                </div>
              </div>
            </>
          )}
          {service && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/services/edit/${service._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                >
                  Edit
                </Link>
                <button
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                  onClick={() => handleDelete(service._id)}
                >
                  Delete
                </button>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">
                {service.service}
              </h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Price</strong>
                  <p>
                    KWD{" "}
                    {new Intl.NumberFormat("en-US").format(service.price) ||
                      "--"}

                      {new Intl.NumberFormat("kw-KW", {
                      style: "currency",
                      currency: "KWD",
                      minimumFractionDigits: 3,
                      maximumFractionDigits: 3,
                    }).format(service.price)}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Date Entered</strong>
                  <p className="capitalize">
                    {new Date(service.date).toLocaleDateString("en-us", {
                      month: "long",
                      year: "numeric",
                      day: "numeric",
                    }) || "--"}
                  </p>
                </span>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default ServicesView;
