import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const Customers = () => {
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customers, setCustomers] = useState([]);
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamcotailor/customers";
  const authKey = localStorage.getItem("jwt");
  const [searchName, setSearchName] = useState("");
  const user = useStoreState((state) => state.user);

  useEffect(() => {
    document.title = "Khamco Shop Customers";

    const fetchCustomers = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomers(response.data.customers);
          setIsLoading(false);
        })
        .catch((error) => {
          setIsLoading(false);
          setCustomers([]);
          setMessage(error.message);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    return () => {
      fetchCustomers();
    };
  }, [apiUrl, authKey, userLanguage, writingDirection]);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[4rem]">
          <div className="utilities">
            <Link to="/customers/new-customer">
              New Customer <i className="fas fa-user-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          <div className="utilities">
            <form action="/customers" method="GET" className="search-engine">
              <input
                type="text"
                placeholder="Search Customer Name"
                name="name"
                className="search-input"
                onChange={(e) => setSearchName(e.target.value)}
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              action="/customers"
              method="GET"
              className="search-engine"
              id="date-form"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form action="/customers" className="search-engine">
              <input
                type="text"
                name="phone"
                id="phone"
                placeholder="Search Customer Phone Number"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form action="/customers" className="search-engine">
              <input
                type="text"
                name="uniqueId"
                placeholder="Search Customer ID"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="title">Customer List</h1>
          {isLoading &&
            new Array(3).fill(
              <div
                className="w-full bg-[#ccc] animate-pulse py-[3rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                dir={writingDirection}
              ></div>
            )}
          {customers && customers.length > 0 ? (
            customers.map((customer, index) => (
              <div
                className="w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>Name</strong>:{" "}
                  {`${customer.name} ${customer.surname}`} {
                    customer.membership_status === "VIP" ? 
                      <i className="fas fa-star blink"></i> : ""
                  }`}
                </p>
                <p className="capitalize">
                  <strong>Last Measurement</strong>:{" "}
                  {customer.measurements.length > 0
                    ? new Date(
                        customer.measurements[0].date_created
                      ).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })
                    : "None"}
                </p>
                <p className="capitalize">
                  <strong>Mobile</strong>:{" "}
                  <a href="tel:<%= customer.phone %>">{customer.phone}</a>
                </p>
                <p className="capitalize">
                  <strong>Dependents</strong>: {customer.dependents.length}
                </p>
                <Link to={`/customers/${customer._id}`} className="edit-btn">
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="text-center">No customer data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Customers;
