import { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import MeasurementList from "../measurements/MeasurementList";

const DependentsDetails = () => {
  const { id } = useParams();
  const { dependentId } = useParams();
  const [customer, setCustomer] = useState();
  const [dependent, setDependent] = useState();
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/dependents/${dependentId}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/dependents/${dependentId}`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Dependent Details";

    const fetchData = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        if (isMounted) {
          setCustomer(response.data.customer);
          setDependent(response.data.dependent);
          setLoading(false);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(false);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey]);

  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.delete(apiUrl, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate(`/customers/${response.data.customer._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };
  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <h1 className="text-center text-[2rem] font-minimoBold">
          Customer Details
        </h1>
        <section className="customer-details">
          {loading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          {customer && dependent && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
                  {customer.surname}
                </Link>
              </div>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                  <Link
                    to={`/customers/edit/${customer._id}/dependents/${dependent._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    Edit
                  </Link>
                  <form onSubmit={handleDelete}>
                    <button
                      type="submit"
                      className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                    >
                      Delete
                    </button>
                  </form>
                  <button
                    className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                    onClick={() => window.print()}
                  >
                    Print <i className="fas fa-print"></i>
                  </button>
                </div>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Dependent of</strong>
                    <p>
                      {customer.name} {customer.surname}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Customer ID</strong>
                    <p>{dependent._id}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Name</strong>
                    <p>
                      {dependent.name} {dependent.surname}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Mobile</strong>
                    <a href={`tel:${dependent.phone}`}>{dependent.phone}</a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Last Measurement</strong>
                    <p>
                      {new Date(dependent.date_created).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Address</strong>
                    <p>{customer.address}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Email</strong>{" "}
                    <a href={`mailto:${dependent.email}`}>{dependent.email}</a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>Status</strong>
                    {dependent.military_status}
                  </span>
                </div>
                {dependent.military_idnumber && (
                  <>
                    <div className="flex flex-col gap-[1rem]">
                      <h2 className="text-center text-[2rem] font-minimoBold">
                        Military Details
                      </h2>
                    </div>
                    <div className="flex flex-col gap-[1rem]">
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>ID Number</strong>
                        <p>{dependent.military_idnumber}</p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Rank</strong>
                        <p>{dependent.military_rank}</p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Department</strong>
                        <p>{dependent.military_department}</p>
                      </span>
                    </div>
                  </>
                )}
                <div className="flex flex-col gap-[1rem]">
                  <h2 className="text-center text-[2rem] font-minimoBold">
                    Measurements
                  </h2>
                </div>
                {dependent.measurements && dependent.measurements.length > 0 ? (
                  dependent.measurements.map((measurement, index) => (
                    <>
                      <MeasurementList key={index} measurement={measurement} />
                      <span className="notes-container flex flex-col justify-start gap-[2rem]">
                        <strong className="notes-container-strong w-full underline">
                          {t("Notes")}:{" "}
                        </strong>
                        {measurement.notes &&
                          measurement.notes.map((item) => (
                            <p className="notes-container-p shadow-md shadow-[#333] p-[1rem] rounded-md bg-[#444] text-[white] flex flex-col gap-[1rem]">
                              <h4 className="notes-container-h4 text-[gold] underline">
                                {item.user || "----"}
                              </h4>
                              <span>{item.text}</span>
                              <span className="notes-container-last-span self-end">
                                {new Date(item.timestamp).toLocaleDateString(
                                  "en-us",
                                  {
                                    day: "numeric",
                                    month: "long",
                                    year: "numeric",
                                  }
                                )}
                                {", "}
                                {new Date(item.timestamp).toLocaleTimeString(
                                  "en-us",
                                  {
                                    hour: "numeric",
                                    minute: "numeric",
                                  }
                                )}
                              </span>
                            </p>
                          ))}
                      </span>
                    </>
                  ))
                ) : (
                  <p className="text-center">
                    Dependent does not have any measurements.
                  </p>
                )}
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default DependentsDetails;
