import { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import axios from "axios";
import { apiUrl, authKey, hasRoles } from "../../utilities";
import { toast } from "react-toastify";
import Selector from "../../components/Selector";
import Footer from "../../components/Footer";

const AdminEditOrder = () => {
  const { id } = useParams();
  const [loadingData, setLoadingData] = useState(true);
  const [loading, setLoading] = useState(false);
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [orderDate, setOrderDate] = useState(null);
  const [orderDetails, setOrderDetails] = useState([]);
  const [assignedBy, setAssignedBy] = useState("");
  const [assignedTo, setAssignedTo] = useState("");
  const [measurer, setMeasurer] = useState("");
  const [measuringDate, setMeasuringDate] = useState(null);
  const [cutter, setCutter] = useState("");
  const [cuttingDate, setCuttingDate] = useState(null);
  const [tailor, setTailor] = useState("");
  const [tailoringDate, setTailoringDate] = useState(null);
  const [driver, setDriver] = useState("");
  const [drivingDate, setDrivingDate] = useState(null);
  const [alterationDate, setAlterationDate] = useState(null);
  const [requiresAlteration, setRequiresAlteration] = useState(false);
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderEndDate, setOrderEndDate] = useState(null);
  const [orderUniqueId, setOrderUniqueId] = useState("");
  const [comments, setOrderComments] = useState({});
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [users, setUsers] = useState([]);
  const [showItems, setShowItems] = useState(false);
  const [totalPrice, setTotalPrice] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        const products_response = await axios.get(`${apiUrl}/products`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        const services_response = await axios.get(`${apiUrl}/services`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        const users_response = await axios.get(`${apiUrl}/admin/tailors`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        setProducts(products_response.data.products);
        setServices(services_response.data.services);
        setUsers(users_response.data.users);

        if (response.data.order) {
          setOrderUniqueId(response.data.order.uniqueId);
          setOrderDate(response.data.order.order_date);
          setOrderDetails(response.data.order.details);
          setAssignedBy(response.data.order.assigned_by);
          setAssignedTo(response.data.order.assigned_to);
          setMeasurer(response.data.order.measurer);
          setMeasuringDate(response.data.order.measuring_date);
          setCutter(response.data.order.cutter);
          setCuttingDate(response.data.order.cutting_date);
          setTailor(response.data.order.tailor);
          setTailoringDate(response.data.order.tailoring_date);
          setDriver(response.data.order.driver);
          setDrivingDate(response.data.order.driving_date);
          setAlterationDate(response.data.order.alteration_date);
          setRequiresAlteration(response.data.order.requires_alteration);
          if (response.data.order.order_end_date) {
            setOrderEndDate(response.data.order.order_end_date);
            setOrderComplete(true);
          }
        }
      } catch (error) {
        if (error.response) {
          if (error.response.status === 403 || error.response.status === 400) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        } else {
          // Handle network errors or errors without a response object
          toast.error("An error occurred: " + error.message);
        }
      } finally {
        setLoadingData(false);
      }
    };

    fetchData();

    const calculatedTotalPrice = orderDetails.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
    setTotalPrice(calculatedTotalPrice);
  }, [id, navigate, orderDetails]);

  const addQuantity = (name, quantity) => {
    setOrderDetails((prevArray) => {
      return prevArray.map((item) => {
        if (item.name === name) {
          const updatedItem = { ...item, quantity: Number(quantity) };
          return updatedItem;
        }
        return item;
      });
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.put(
        `${apiUrl}/admin/orders/${id}`,
        {
          order_date: orderDate,
          details: orderDetails,
          assigned_by: assignedBy,
          assigned_to: assignedTo,
          measurer: measurer,
          measuring_date: measuringDate,
          cutter: cutter,
          cutting_date: cuttingDate,
          tailor: tailor,
          tailoring_date: tailoringDate,
          driver: driver,
          driving_date: drivingDate,
          alteration_date: alterationDate,
          requires_alteration: requiresAlteration,
          order_complete: orderComplete,
          order_end_date: orderEndDate,
          comments: comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      navigate("/admin/orders");
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const validateFieldRules = (field, value) => {
    if (field === "Measurer") {
      setMeasurer(value);
      if (value) {
        // Reset others when Measurer is selected
        setCutter("");
        setCuttingDate(null);
        setTailor("");
        setTailoringDate(null);
        setDriver("");
        setDrivingDate(null);
        setAlterationDate(null);
        setRequiresAlteration(false);
        setOrderComplete(false);
        setOrderEndDate(null);
      }
    } else if (field === "Cutter") {
      setCutter(value);
      if (value) {
        // Reset others when Cutter is selected
        setMeasurer("");
        setMeasuringDate(null);
        setTailor("");
        setTailoringDate(null);
        setDriver("");
        setDrivingDate(null);
        setAlterationDate(null);
        setRequiresAlteration(false);
        setOrderComplete(false);
        setOrderEndDate(null);
      }
    } else if (field === "Tailor") {
      setTailor(value);
      if (value) {
        // Reset others when Tailor is selected
        setMeasurer("");
        setMeasuringDate(null);
        setCutter("");
        setCuttingDate(null);
        setDriver("");
        setDrivingDate(null);
        setAlterationDate(null);
        setRequiresAlteration(false);
        setOrderComplete(false);
        setOrderEndDate(null);
      }
    } else if (field === "Driver") {
      setDriver(value);
      if (value) {
        // Reset others when Driver is selected
        setMeasurer("");
        setMeasuringDate(null);
        setCutter("");
        setCuttingDate(null);
        setTailor("");
        setTailoringDate(null);
        setAlterationDate(null);
        setRequiresAlteration(false);
        setOrderComplete(false);
        setOrderEndDate(null);
      }
    } else if (field === "Alteration") {
      setRequiresAlteration(value);
      if (value) {
        // Reset others when Alteration is selected
        setMeasurer("");
        setMeasuringDate(null);
        setCutter("");
        setCuttingDate(null);
        setTailor("");
        setTailoringDate(null);
        setDriver("");
        setDrivingDate(null);
        setOrderComplete(false);
        setOrderEndDate(null);
      }
    } else if (field === "Complete") {
      setOrderComplete(value);
      if (value) {
        // Reset others when Complete is selected
        setMeasurer("");
        setMeasuringDate(null);
        setCutter("");
        setCuttingDate(null);
        setTailor("");
        setTailoringDate(null);
        setDriver("");
        setDrivingDate(null);
        setRequiresAlteration(false);
        setAlterationDate(null);
      }
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section className="font-minimoBold">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/admin/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> {t("Orders")}
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">
            {`${t("Edit Order")} - ${orderUniqueId || "--"}`}
          </h1>
          {loadingData && (
            <>
              {new Array(4).fill(null).map((_, index) => (
                <div
                  key={index}
                  className="bg-[#ccc] animate-pulse w-full py-[2rem] rounded-md md:w-1/2 flex flex-col mx-auto my-[2rem] relative"
                ></div>
              ))}
            </>
          )}
          {!loadingData && (
            <form
              className="flex flex-col justify-center items-center m-auto w-full"
              onSubmit={handleSubmit}
            >
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">Order Date</label>
                <input
                  type="date"
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={
                    orderDate
                      ? new Date(orderDate).toISOString().split("T")[0]
                      : ""
                  }
                  onChange={(e) => setOrderDate(e.target.value)}
                />
              </div>
              {/* Products and Services Start */}
              <button
                className="bg-khamco-primary py-1 px-[1rem] min-w-10 rounded-full text-[1rem] text-white text-center font-bold border-2 border-khamco-secondary"
                type="button"
                onClick={() => {
                  setShowItems(!showItems);
                }}
                title={showItems ? "Hide input fields" : "Show input fields"}
              >
                {showItems ? "Hide items" : "Show items"}
              </button>
              {showItems && (
                <>
                  <h1 className="text-center my-[1rem] text-xl font-bold">
                    Select Products
                  </h1>
                  <div className="flex gap-[1rem] w-full md:w-2/4 justify-center md:justify-start flex-wrap">
                    {products &&
                      products.map((product, index) => (
                        <div className="flex flex-col items-center">
                          <Selector
                            key={index}
                            label={product.name}
                            price={product.price}
                            quantity={product.quantity}
                            type="product"
                            setOptions={setOrderDetails}
                            options={orderDetails}
                          />
                          <p>
                            {t("Available")}:{" "}
                            <span
                              className={`text-lg ${
                                product.quantity <= 0 && "text-[red] font-bold"
                              }`}
                            >
                              {product.quantity.toFixed(0)}
                            </span>
                          </p>
                        </div>
                      ))}
                  </div>
                  <h1 className="text-center my-[1rem] text-xl font-bold">
                    Select Services
                  </h1>
                  <div className="flex flex-col gap-[1rem] md:w-2/4 ">
                    <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                      {services &&
                        services.map((service, index) => (
                          <Selector
                            key={index}
                            label={service.service}
                            price={service.price}
                            quantity={1}
                            type="service"
                            setOptions={setOrderDetails}
                            options={orderDetails}
                          />
                        ))}
                    </div>
                    {orderDetails.length > 0 && (
                      <p className="text-center my-[1rem] font-bold text-lg text-[red]">
                        {t("Enter quantities below")}
                      </p>
                    )}
                  </div>
                </>
              )}
              {/* Products and Services End */}
              <div className="w-full md:w-2/4 flex flex-col mx-auto my-4 relative">
                {orderDetails.length > 0 && (
                  <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem] overflow-auto">
                    <thead className="text-khamco-primary font-bold bg-[#ddd]">
                      <tr className="border-[0.2rem] border-khamco-secondary relative">
                        <th className="border-r-2 border-[blue]">
                          {t("Item")}
                        </th>
                        <th className="border-r-2 border-[blue]">
                          {t("Price")}
                        </th>
                        <th className="border-r-2 border-[blue]">
                          {t("Quantity")}
                        </th>
                        <th className="border border-gray-300 p-2">
                          {t("Total")}
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {orderDetails.map((item, index) => (
                        <tr
                          key={index}
                          className="border-[0.2rem] border-khamco-secondary border-t-0 relative"
                        >
                          <td className="p-[0.5rem] border-r-2 border-[blue]">
                            {item.name}
                          </td>
                          <td className="text-center p-[0.5rem] border-r-2 border-[blue]">
                            KWD{" "}
                            {new Intl.NumberFormat("en-US").format(item.price)}
                          </td>
                          <td className="border-r-2 border-[blue]">
                            <input
                              type="number"
                              value={item.quantity}
                              onChange={(e) =>
                                addQuantity(item.name, e.target.value)
                              }
                              className="text-center w-full border-2 border-black outline-none focus:border-khamco-primary"
                            />
                          </td>
                          <td className="text-center p-[0.5rem]">
                            {new Intl.NumberFormat("en-US").format(
                              item.price * item.quantity
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                    <tfoot>
                    <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                      <td
                        colSpan="3"
                        className="border border-gray-300 font-bold text-center p-[0.5rem]"
                      >
                        {t("Total Price")} (KWD)
                      </td>
                      <td className="border border-gray-300 font-bold text-center p-[0.5rem]">
                        {new Intl.NumberFormat("en-US").format(totalPrice)}
                      </td>
                    </tr>
                    <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                      <td
                        colSpan="3"
                        className="border border-gray-300 font-bold text-center p-[0.5rem]"
                      >
                        {t("Amount Remaining")} (KWD)
                      </td>
                    </tr>
                  </tfoot>
                  </table>
                )}
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Assigned By
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={assignedBy || ""}
                  onChange={(e) => setAssignedBy(e.target.value)}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users.map((user, index) => (
                      <option
                        key={index}
                        value={`${user.name} ${user.surname}`}
                      >{`${user.name} ${user.surname}`}</option>
                    ))}
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Assigned To
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={assignedTo || ""}
                  onChange={(e) => setAssignedTo(e.target.value)}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users.map((user, index) => (
                      <option
                        key={index}
                        value={`${user.name} ${user.surname}`}
                      >{`${user.name} ${user.surname}`}</option>
                    ))}
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Select The Measurer
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={measurer || ""}
                  onChange={(e) => {
                    validateFieldRules("Measurer", e.target.value);
                  }}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users
                      .filter((user) => hasRoles(["Measurer"], user))
                      .map((user, index) => (
                        <option
                          key={index}
                          value={`${user.name} ${user.surname}`}
                        >{`${user.name} ${user.surname}`}</option>
                      ))}
                </select>
              </div>
              {measurer && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Measuring Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      measuringDate
                        ? new Date(measuringDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setMeasuringDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Select The Cutter
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={cutter || ""}
                  onChange={(e) => {
                    validateFieldRules("Cutter", e.target.value);
                  }}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users
                      .filter((user) => hasRoles(["Cutter"], user))
                      .map((user, index) => (
                        <option
                          key={index}
                          value={`${user.name} ${user.surname}`}
                        >{`${user.name} ${user.surname}`}</option>
                      ))}
                </select>
              </div>
              {cutter && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Cutting Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      cuttingDate
                        ? new Date(cuttingDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setCuttingDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Select The Tailor
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={cutter || ""}
                  onChange={(e) => {
                    validateFieldRules("Tailor", e.target.value);
                  }}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users
                      .filter((user) => hasRoles(["Tailor"], user))
                      .map((user, index) => (
                        <option
                          key={index}
                          value={`${user.name} ${user.surname}`}
                        >{`${user.name} ${user.surname}`}</option>
                      ))}
                </select>
              </div>
              {tailor && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Tailoring Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      tailoringDate
                        ? new Date(tailoringDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setTailoringDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Select The Driver
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={driver || ""}
                  onChange={(e) => {
                    validateFieldRules("Driver", e.target.value);
                  }}
                >
                  <option value="">-- Select from the list --</option>
                  {users &&
                    users
                      .filter((user) => hasRoles(["Driver"], user))
                      .map((user, index) => (
                        <option
                          key={index}
                          value={`${user.name} ${user.surname}`}
                        >{`${user.name} ${user.surname}`}</option>
                      ))}
                </select>
              </div>
              {driver && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Driving Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      drivingDate
                        ? new Date(drivingDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setDrivingDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex gap-[1rem] mx-auto my-4 relative">
                <label
                  htmlFor="requires-alteration"
                  className="cursor-pointer text-lg my-2 select-none self-start font-medium"
                >
                  Requires Alteration?
                </label>
                <input
                  id="requires-alteration"
                  type="checkbox"
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] w-[2rem]"
                  onChange={() => {
                    validateFieldRules("Alteration", !requiresAlteration);
                  }}
                />
              </div>
              {requiresAlteration && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Alteration Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      alterationDate
                        ? new Date(alterationDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setAlterationDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex gap-[1rem] mx-auto my-4 relative">
                <label
                  htmlFor="order-complete"
                  className="cursor-pointer text-lg my-2 select-none self-start font-medium"
                >
                  Order Complete?
                </label>
                <input
                  id="order-complete"
                  type="checkbox"
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] w-[2rem]"
                  onChange={() => {
                    validateFieldRules("Complete", !orderComplete);
                  }}
                />
              </div>
              {orderComplete && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Order Completion Date
                  </label>
                  <input
                    type="date"
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    value={
                      orderEndDate
                        ? new Date(orderEndDate).toISOString().split("T")[0]
                        : ""
                    }
                    onChange={(e) => setOrderEndDate(e.target.value)}
                  />
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Add a Comment
                </label>
                <textarea
                  type="date"
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  placeholder="Write a comment"
                  onChange={(e) => setOrderComments(e.target.value)}
                ></textarea>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                  disabled={loading}
                >
                  {loading ? (
                    <i className="fas fa-spinner animate-spin"></i>
                  ) : (
                    `${t("Submit")}`
                  )}
                </button>
              </div>
            </form>
          )}
        </section>
      </main>
      <Footer />
      {showItems && (
        <div className="flex gap-[2rem] p-[0.5rem] rounded-md fixed top-[50%] right-[10%] bg-[#333]">
          <div className="bg-khamco-primary py-1 px-[1rem] min-w-10 rounded-full text-[1.5rem] text-white text-center font-bold border-2 border-khamco-secondary">
            {loadingData ? (
              <i className="fas fa-spinner animate-spin"></i>
            ) : (
              orderDetails.length
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default AdminEditOrder;
