import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import { useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";

const NewInvoice = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  // API Data
  const [services, setServices] = useState([]);
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState({});
  const [customer, setCustomer] = useState({});
  const [invoice, setInvoice] = useState({});
  // Form fields
  const [serviceChoice, setServiceChoice] = useState([]);
  const [serviceQuantity, setServiceQuantity] = useState(null);
  const [servicePrice, setServicePrice] = useState(null);
  const [productChoice, setProductChoice] = useState([]);
  const [productQuantity, setProductQuantity] = useState(null);
  const [productPrice, setProductPrice] = useState(null);
  const [dependentName, setDependentName] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState(null);
  const [totalPrice, setTotalPrice] = useState(null);
  const [date, setDate] = useState(null);
  const [loading, setLoading] = useState(false);

  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamcotailor/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Invoice - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/new-invoice/data`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        setProducts(response.data.products);
        setServices(response.data.services);
        setCust(response.data.services);
        console.log(response.data);
      } catch (error) {
        toast.error(error.response?.data?.message || error.message);
      }
    };

    fetchData();
  }, [apiUrl, authKey, userLanguage]);

  const removeProduct = async (productId) => {
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/${customer._id}/${invoice._id}/products/${productId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const addProduct = async () => {
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${customer._id}/${invoice._id}/products`,
        {
          product_choice: productChoice,
          product_quantity: productQuantity,
          product_price: productPrice,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setInvoice(response.data.invoice);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const removeService = async (serviceId) => {
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/${customer._id}/${invoice._id}/services/${serviceId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const addService = async () => {
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${customer._id}/${invoice._id}/services`,
        {
          service_choice: serviceChoice,
          service_quantity: serviceQuantity,
          service_price: servicePrice,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setInvoice(response.data.invoice);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const removeDependent = async (dependentId) => {
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/${customer._id}/${invoice._id}/dependents/${dependentId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const addDependent = async () => {
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${customer._id}/${invoice._id}/dependents`,
        {
          service_choice: serviceChoice,
          service_quantity: serviceQuantity,
          service_price: servicePrice,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setProducts(response.data.products);
      setServices(response.data.services);
      setCustomer(response.data.customer);
      setInvoice(response.data.invoice);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const deleteInvoice = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.delete(`${apiUrl}/${invoice._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/invoices");
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = axios.post(
        apiUrl,
        {
          service_choice: serviceChoice,
          service_quantity: serviceQuantity,
          service_price: servicePrice,
          product_choice: productChoice,
          product_quantity: productQuantity,
          product_price: productPrice,
          dependent_name: dependentName,
          customer_name: customerName,
          customer_surname: customerSurname,
          customer_phone: customerPhone,
          payment_type: paymentType,
          payment_status: paymentStatus,
          amount_paid: amountPaid,
          total_price: totalPrice,
          date: date,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setInvoice(response.data.invoice);
      setCustomer(response.data.customer);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="khamco-wrapper font-minimoBold"
          dir={writingDirection}
        >
          <div className="utilities">
            <Link to="/invoices">
              <i className="fas fa-arrow-left"></i> Invoices
            </Link>
          </div>
          <h1 className="title">
            New Invoice
          </h1>
          <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem] overflow-auto">
            <thead className="text-khamco-primary font-bold">
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="bg-[#ddd] text-center p-[0.5rem]">Item</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">Quantity</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">Available</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">Price/Unit</td>
                <td className="bg-[#ddd] text-center p-[0.5rem]">
                  Total Price
                </td>
              </tr>
            </thead>
            <tbody>
              {invoice.products &&
                invoice.products.map((product, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() => removeProduct(product._id)}
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <>
                            <i className="fas fa-minus"></i>
                          </>
                        )}
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {product.choice}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {product.quantity}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      {product.quantity}
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {product.price}
                      </span>
                    </td>
                    <td className="text-center p[0.5rem]">
                      KWD{" "}
                      {new Intl.NumberFormat("en-US").format(
                        product.quantity * product.price
                      )}
                    </td>
                  </tr>
                ))}
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button className="add-data" onClick={addProduct}>
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem]"
                    onChange={(e) => setProductChoice(e.target.value)}
                  >
                    <option label="-- Select a product --"></option>
                    {products &&
                      products.map((product, index) => (
                        <option
                          key={index}
                          value={product.name}
                          label={product.name}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    step="0.01"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    placeholder="Product quantity"
                    onChange={(e) => setProductQuantity(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setProductPrice(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              {invoice.services &&
                invoice.services.map((service, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() => removeService(service._id)}
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <>
                            <i className="fas fa-minus"></i>
                          </>
                        )}
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.choice}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.quantity}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]">
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {service.price}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]">
                      {service.quantity * service.price}
                    </td>
                  </tr>
                ))}
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button className="add-data" onClick={addService}>
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem]"
                    onChange={(e) => setServiceChoice(e.target.value)}
                  >
                    <option label="-- Select a service --"></option>
                    {services &&
                      services.map((service, index) => (
                        <option
                          key={index}
                          value={service.service}
                          label={service.service}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    placeholder="Service quantity"
                    onChange={(e) => setServiceQuantity(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    10.9
                  </span>
                </td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <strong>Total Invoice</strong>
                </td>
                <td>
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {invoice.total_price}
                  </span>
                </td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative text-khamco-primary font-bold">
                <td className="text-center p-[0.5rem]">Customer Details</td>
                <td className="text-center p-[0.5rem]"><select
                    className="w-full p-[0.5rem] text-center text-[1.2rem]"
                    onChange={(e) => setCustomerName(e.target.value)}
                  >
                    {customers && customers.map((customer, index) => (
                      <option key={index} value={customer.name}>{customer.name}</option>
                    ))}
                  </select></td>
                <td className="text-center p-[0.5rem]">{customer.surname}</td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              {invoice.dependents &&
                invoice.dependents.map((dependent, index) => (
                  <tr
                    className="border-t-0 border-[0.2rem] border-khamco-secondary relative"
                    key={index}
                  >
                    <td className="text-center p-[0.5rem]">
                      <button
                        className="remove-data"
                        onClick={() => removeDependent(dependent._id)}
                      >
                        <i className="fas fa-minus"></i>
                      </button>
                      <span className="p-[0.5rem] text-[1.2rem] text-center">
                        {dependent.customer_name}
                      </span>
                    </td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                    <td className="text-center p-[0.5rem]"></td>
                  </tr>
                ))}
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {customer && customer.name}
                  </span>
                </td>
                <td className="text-center p-[0.5rem]">
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {customer && customer.surname}
                  </span>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button className="add-data" onClick={addDependent}>
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>
                        <i className="fas fa-plus"></i>
                      </>
                    )}
                  </button>
                  <select
                    className="w-full p-[0.5rem] text-center text-[1.2rem]"
                    onChange={(e) => setDependentName(e.target.value)}
                  >
                    <option label="-- Select a dependent --" readOnly></option>
                    {customer.dependents &&
                      customer.dependents.map((dependent, index) => (
                        <option
                          key={index}
                          value={`${dependent.customer_name} ${dependent.customer_surname}`}
                          label={`${dependent.customer_name} ${dependent.customer_surname}`}
                        ></option>
                      ))}
                  </select>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <span className="p-[0.5rem] text-[1.2rem] text-center">
                    {customer.phone}
                  </span>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative text-khamco-primary font-bold">
                <td className="text-center p-[0.5rem]">Company</td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">Date</td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <input
                  type="text"
                  name="product_choice"
                  id="productChoiceInfo"
                  className="hidden"
                />
                <input
                  type="text"
                  name="product_quantity"
                  id="productQuantityInfo"
                  className="hidden"
                />
                <input
                  type="number"
                  step="0.01"
                  name="product_price"
                  id="productPriceInfo"
                  className="hidden"
                />
                <input
                  type="text"
                  name="service_choice"
                  id="serviceChoiceInfo"
                  className="hidden"
                />
                <input
                  type="text"
                  name="service_quantity"
                  id="serviceQuantityInfo"
                  className="hidden"
                />
                <input
                  type="number"
                  step="0.01"
                  placeholder="Service Price"
                  className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  onChange={(e) => setServicePrice(e.target.value)}
                />
                <input
                  type="text"
                  onChange={(e) => setCustomerName(e.target.value)}
                  className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  placeholder="Customer Name"
                />
                <input
                  type="text"
                  value={customer.surname}
                  onChange={(e) => setCustomerSurname(e.target.value)}
                  className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  placeholder="Customer Surname"
                />
                <input
                  type="text"
                  value={customer.phone}
                  onChange={(e) => setCustomerPhone(e.target.value)}
                  className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  placeholder="Customer Phone"
                />
                <input
                  type="text"
                  name="dependent_name"
                  id="dependentNameInfo"
                  className="hidden"
                />
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">Khamco</td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]">
                  <input
                    type="date"
                    value={
                      invoice.date == null
                        ? ""
                        : invoice.date.toISOString().split("T")[0]
                    }
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setDate(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <select
                    className="w-full p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setPaymentStatus(e.target.value)}
                  >
                    <option value="Unpaid">Unpaid</option>
                    <option value="Partially Paid">Partially Paid</option>
                    <option value="Paid">Paid</option>
                    <option value="Other">Other</option>
                  </select>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <input
                    type="number"
                    step="0.01"
                    value={invoice.amount_paid}
                    placeholder="Amount Paid"
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                    onChange={(e) => setAmountPaid(e.target.value)}
                  />
                </td>
                <td className="text-center p-[0.5rem]">
                  <select
                    name="payment_type"
                    onChange={(e) => setPaymentType(e.target.value)}
                    className="p-[0.5rem] text-[1.2rem] text-center border border-black rounded-md"
                  >
                    <option value="K-Net">K-Net</option>
                    <option value="Cash">Cash</option>
                    <option value="Other">Other</option>
                  </select>
                </td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
                <td className="text-center p-[0.5rem]"></td>
              </tr>
              <tr className="border-t-0 border-[0.2rem] border-khamco-secondary relative">
                <td className="text-center p-[0.5rem]">
                  <button
                    className="edit-btn w-full py-[1rem] px-[2rem]"
                    onClick={handleSubmit}
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      <>Submit</>
                    )}
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
          <div className="row">
            <form onSubmit={deleteInvoice}>
              <button
                type="submit"
                className="bg-[red] py-[0.5rem] px-[1rem] fixed top-[50%] right-2 rounded-full text-[1.5rem] text-white hover:rotate-12 transition-all"
              >
                {loading ? (
                  <i className="fas fa-spinner animate-spin"></i>
                ) : (
                  <>
                    <i className="fas fa-trash-can"></i>
                  </>
                )}
              </button>
            </form>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewInvoice;
