import { useEffect, useState } from "react";

const Clothing = ({ label, imageUrl, setOptions, options }) => {
  const [isChecked, setIsChecked] = useState(false);
  // Generate a unique ID for each Clothing component
  const inputId = `clothing_item_${label.replace(/\s+/g, "_").toLowerCase()}`;

  // useEffect(() => {
  //   if (options.some((article) => article.clothing_item === label)) {
  //     setIsChecked(true);
  //   } else {
  //     setIsChecked(false);
  //   }
  // }, [label, options]);

  const add = () => {
    if (isChecked) {
      setOptions((prevArray) =>
        prevArray.filter((item) => item.clothing_item !== label)
      );
    } else {
      setOptions((prevArray) => {
        // Check if item already exists
        const exists = prevArray.some((item) => item.clothing_item === label);
        if (!exists) {
          return [...prevArray, { clothing_item: label }];
        }

        return prevArray;
      });
    }
  };

  return (
    <>
      <label
        htmlFor={inputId}
        className={`p-2 w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 ${
          isChecked ? "border-2 border-khamco-primary bg-[#ccc]" : "bg-[white]"
        }`}
        onClick={add}
      >
        <img src={imageUrl} alt={label} />
        <p className="text-sm">{label}</p>
        <input
          type="checkbox"
          id={inputId}
          onChange={() => {
            setIsChecked(!isChecked);
          }}
          // checked={isChecked}
          className="accent-khamco-primary"
        />
      </label>
    </>
  );
};

export default Clothing;
