import { useEffect, useRef, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import OrderForm from "./OrderForm";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";
import CheckBox from "../../components/CheckBox";

const NewOrder = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [order, setOrder] = useState([]);
  const [loadingData, setLoadingData] = useState(true);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [orderName, setOrderName] = useState([]);
  const orderNameRef = useRef(null);
  const [dependentList, setDependentList] = useState([]);
  const dependentNameRef = useRef(null);
  const [customerName, setCustomerName] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [customerDependents, setCustomerDependents] = useState([]);
  const [customerAddress, setCustomerAddress] = useState([]);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [sewer, setSewer] = useState("");
  const [orderStatus, setOrderStatus] = useState("");
  const [orderNotes, setOrderNotes] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const userLanguage = useStoreState((state) => state.userLanguage);

  useEffect(() => {
    let isMounted = true;

    document.title = "New Order - Khamco Shop";

    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get(
          `${apiUrl}/orders/create/new-order/${id}`,
          {
            headers: { Authorization: `Bearer ${authKey}` },
          }
        );
        setLoadingData(false);
        setOrder(response.data.order);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
      } catch (error) {
        if (isMounted) {
          setLoadingData(false);
          toast.error(error.response?.data?.message || error.message);
          console.error(error.message);
        }
      }
    };

    fetchOrderDetails();

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, userLanguage]);

  const createOrderName = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (!orderName) {
      setLoading(false);
      toast.warn("Please select a value");
      return;
    }
    await axios
      .put(
        `${apiUrl}/orders/${id}/name`,
        {
          order_name: orderName,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setOrder(response.data.order);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
        setLoadingData(false);
        setOrderName([]);
        orderNameRef.current.value = "";
      })
      .catch((error) => {
        setLoadingData(false);
        toast.error(error.response?.data?.message || error.message);
        console.error(error.message);
      });
  };

  const deleteOrderName = async (e, orderId, nameId) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/orders/${orderId}/names/${nameId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setOrder(response.data.order);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleChange = async (e) => {
    const selectedOption = e.target.options[e.target.selectedIndex];

    if (e.target.value === "-- Select customer --") {
      setCustomerDependents([]);
      return;
    }

    if (selectedOption) {
      setCustomerEmail(selectedOption.dataset.email);
      setCustomerPhone(selectedOption.dataset.phone);
      setCustomerAddress(selectedOption.dataset.address);
      setCustomerName(selectedOption.value);
      setCustomerDependents(
        (e.target.value = customers.find(
          (customer) => customer.name === e.target.value
        )).dependents
      );
    }
  };

  const deleteEntireOrder = async () => {
    setLoading(true);
    try {
      const response = await axios.delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      setLoading(false);
      navigate("/orders");
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const deleteDependent = async (e, orderId, customerId) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/orders/${orderId}/dependents/${customerId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setOrder(response.data.order);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!orderStatus) {
      toast.warn("The order status is required.");
      return;
    }

    if (!customerName) {
      toast.warn("Please select a customer");
      return;
    }

    if (!orderName) {
      toast.warn("Please select a product or service from the list");
      return;
    }
    setLoading(true);
    await axios
      .put(
        `${apiUrl}/orders/${order._id}`,
        {
          order_name: orderName,
          dependent_name: dependentList,
          order_start_time: new Date(),
          customer_name: customerName,
          customer_email: customerEmail,
          customer_phone: customerPhone,
          customer_address: customerAddress,
          measurer: measurer,
          cutter: cutter,
          sewer: sewer,
          order_status: orderStatus,
          order_notes: orderNotes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        window.location.replace(`/orders/${response.data.order._id}`);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center relative"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> Orders
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">New Order</h1>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <h1 className="text-center my-[1rem] text-xl font-bold">
              Product List
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading products...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-start flex-wrap">
              {products &&
                products.map((product, index) => (
                  <>
                    <CheckBox
                      key={index}
                      label={product.name}
                      imageUrl={null}
                      setOptions={setOrderName}
                    />
                  </>
                ))}
            </div>
            <h1 className="text-center my-[1rem] text-xl font-bold">
              Service List
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading services...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-start flex-wrap">
              {services &&
                services.map((service, index) => (
                  <CheckBox
                    key={index}
                    label={service.service}
                    imageUrl={null}
                    setOptions={setOrderName}
                  />
                ))}
            </div>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              Customer Name
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              onChange={handleChange}
            >
              <option
                label="-- Select customer --"
                value="-- Select customer --"
              ></option>
              {customers &&
                customers.map((customer, index) => (
                  <option
                    key={index}
                    value={customer.name}
                    label={`${customer.name} ${customer.surname}`}
                    data-email={customer.email}
                    data-phone={customer.phone}
                    data-address={customer.address}
                  ></option>
                ))}
            </select>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <div className="flex gap-[1rem] w-full justify-start flex-wrap">
            {customerDependents.length > 0 &&<h1>Selected Dependents</h1>}
              {customerDependents.length > 0 &&
                customerDependents.map((dependent, index) => (
                  <CheckBox
                    key={index}
                    label={dependent.name}
                    imageUrl={null}
                    setOptions={setDependentList}
                  />
                ))}
              {customerDependents.length <= 0 && <p>No dependents...</p>}
            </div>
          </div>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <OrderForm
              customerName={customerName}
              customerEmail={customerEmail}
              customerPhone={customerPhone}
              customerAddress={customerAddress}
              orderId={id}
              loading={loading}
              setMeasurer={setMeasurer}
              setCutter={setCutter}
              setSewer={setSewer}
              setOrderStatus={setOrderStatus}
              setOrderNotes={setOrderNotes}
            />
          </form>
          <button
            className="bg-[red] py-[0.5rem] px-[1rem] fixed top-[50%] rounded-full text-[1.5rem] text-white hover:rotate-12 transition-all"
            disabled={loading}
            onClick={deleteEntireOrder}
            title="Delete this order"
          >
            {loading ? (
              <i className="fas fa-spinner animate-spin"></i>
            ) : (
              <>
                <i className="fas fa-trash-can"></i>
              </>
            )}
          </button>
          {orderName.length > 0 && (
            <button className="bg-khamco-primary py-[0.5rem] px-[1.2rem] fixed top-[50%] right-[10%] rounded-full text-[1.5rem] text-white font-bold border-2 border-khamco-secondary">
              {orderName.length || 0}
            </button>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewOrder;
