import React, { useState } from 'react'
import Navbar from '../../components/Navbar';
import UserForm from './UserForm';

const CreateMaster = () => {
    const [message, setMessage] = useState("");
    const [checkMaster, setCheckMaster] = useState(null);
    const [authorizedUser, setAuthorizedUser] = useState(null);

  return (
    <><Navbar /><main>
          <p class="message">
              {message}
          </p>
          <section class="khamco-wrapper">
              <h1 class="title">New Master Admin</h1>
              <form action="/admin/register-master" method="POST">
                  <div class="row">
                      <p class="alert">There can only be one <strong>Master Administrator</strong>. A user with these
                          privileges has access to all resources. <i class="fas fa-user-plus"></i></p>
                  </div>
                  <UserForm checkMaster={checkMaster} authorizedUser={authorizedUser} />
              </form>
          </section>
      </main></>
  )
}

export default CreateMaster
