import { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import logo from "../../assets/images/logo.jpg";
import { useStoreState } from "easy-peasy";
import { useTranslation } from "react-i18next";

const ShowInvoice = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [dialogState, setDialogState] = useState(false);
  const [order, setOrder] = useState([]);
  const [invoice, setInvoice] = useState();
  const [customer, setCustomer] = useState(null);
  const [customers, setCustomers] = useState("");
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const invoiceRef = useRef(null);
  const { t } = useTranslation();

  useEffect(() => {
    let isMounted = true;

    document.title = "Invoice Details - Khamco Shop";

    const fetchInvoice = async () => {
      try {
        const response = await axios.get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setInvoice(response.data.invoice);
          setLoading(false);
          setOrder(response.data.order);
          setCustomer(response.data.customer);
          setCustomers(response.data.customers);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(true);
          setInvoice({});
          toast.error(error.response?.data?.message || error.message);
        }
      }
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchInvoice();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, userLanguage]);

  const deleteInvoice = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/${invoice._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/invoices");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${id}`,
        {
          payment_type: paymentStatus,
          amount_paid: amountPaid,
          payment_status: paymentType,
          customer_name: customerName,
          customer_phone: phoneNumber,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setCustomer(response.data.customer);
      setOrder(response.data.order);
      toast.success(response.data.message);
      setDialogState(false);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = invoiceRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Invoice</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-bottom: 2.5rem;
            }
            table, th, td {
              border: 4px solid blue;
            }
            th, td {
              padding: 10px;
              text-align: left;
            }
            .invoice-data{
              display: flex;
              justify-content: flex-end;
              flex-wrap;
              gap: 2rem;
              align-items: center;
              margin: 1rem 0;
              padding: 0 0.2rem; 
            }
            .invoice-data strong {
              color: blue;
              font-size: 1.5rem;
            }
            .invoice-data input {
              border: 0.2rem solid black;
              padding: 0.2rem;
              font-size: 1.25rem;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"> </i> {t("Invoices")}
            </Link>
          </div>
          {loading && (
            <>
              <table className="text-center">
                <thead>
                  <tr>
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {invoice && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <form onSubmit={deleteInvoice}>
                  <button
                    type="submit"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                    title="Delete this invoice"
                  >
                    {t("Delete")}
                  </button>
                </form>
                <button
                  title="Share to Whatsapp"
                  className="bg-[limegreen] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer"
                >
                  <i className="fab fa-whatsapp fa-2x"></i>
                </button>
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={handlePrint}
                >
                  {t("Print")} <i className="fas fa-print"></i>
                </button>
                {order ? (
                  <Link
                    to={`/orders/${order._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    {t("View Order")}
                  </Link>
                ) : (
                  <p className="message">No Orders</p>
                )}
                <button
                  className="bg-[gray] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:bg-[goldenrod]"
                  onClick={() => setDialogState(true)}
                >
                  {t("Change Payment Details")}: {invoice.payment_status}{" "}
                  <i className="fas fa-money-check"></i>
                </button>
              </div>
              <div
                className="w-full m-auto shadow-md shadow-[#666]"
                ref={invoiceRef}
              >
                <div className="bg-white m-auto px-0">
                  <div>
                    <span className="w-full flex justify-evenly items-center m-auto">
                      <img src={logo} alt="Logo" />
                    </span>
                  </div>
                  <div className="details">
                    <table
                      className="border-collapse w-full my-[1rem] m-auto text-[1.2rem]"
                      id="receipt-table"
                    >
                      <thead className="text-khamco-primary font-bold">
                        <tr className="border-[0.2rem] border-khamco-secondary border-t-0">
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Item")}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Price")}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {Quantity}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            Total
                          </td>
                        </tr>
                      </thead>
                      <tbody>
                        {invoice.details &&
                          invoice.details.map((item, index) => (
                            <tr
                              className="border-[0.2rem] border-khamco-secondary border-t-0"
                              key={index}
                            >
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.name || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.price || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.quantity || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.quantity * item.price}
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </table>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem]">
                      <strong className="text-khamco-secondary text-2xl">
                        Total Price
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.total_price
                        )}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Amount Paid
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_paid
                        )}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Amount Remaining
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_remaining
                        )}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className=" text-khamco-secondary text-2xl">
                        Date
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`${new Date(invoice.date).toLocaleDateString("en-us", {
                          month: "long",
                          day: "2-digit",
                          year: "numeric",
                        })}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className=" text-khamco-secondary text-2xl">
                        Invoice No.
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`${invoice.uniqueId}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Payment Type
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`${invoice.payment_type}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Customer ID
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`${customer && customer.uniqueId}`}
                      </span>
                    </div>
                    <div className="flex justify-start gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data">
                      <strong className="text-khamco-secondary text-2xl">
                        Customer Phone
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-lg">
                        {`${
                          invoice.customer_phone &&
                          !invoice.customer_phone.startsWith("+965")
                            ? "+965" + invoice.customer_phone
                            : invoice.customer_phone
                        }`}
                      </span>
                    </div>
                    <div className="flex justify-center flex-wrap items-center gap-[2rem] py-[2rem]">
                      <span className="text-lg flex items-center gap-[1rem]">
                        <i className="fas fa-phone-volume text-4xl"></i>
                        <p>
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="tel:+96522400650"
                          >
                            (+965) 22400650
                          </a>{" "}
                          -{" "}
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="tel:+96555627017"
                          >
                            (+965) 55627017
                          </a>
                        </p>
                      </span>
                      <span className="text-lg flex items-center gap-[1rem]">
                        <i className="fas fa-envelope text-4xl"></i>
                        <p>
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="mailto:info@kham.co"
                          >
                            info@kham.co
                          </a>{" "}
                          - @khamco.kw
                        </p>
                      </span>
                      <address className="text-[#666] mx-[0.5rem]">
                      Khamco Company, LLC - PO Box 21353, Safat 13074, Qibla, Kuwait
                      </address>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={dialogState}
        >
          <div
            className="p-[4rem] grid justify-center items-center"
            dir={writingDirection}
          >
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={handleSubmit}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer absolute top-[5%] left-[5%] hover:opacity-90"
                type="button"
                onClick={() => setDialogState(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Change Payment Details
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_status"
                >
                  Payment Status ({invoice ? invoice.payment_status : ""})
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  onChange={(e) => setPaymentStatus(e.target.value)}
                >
                  <option value="-- Select payment status --">
                    -- Select payment status --
                  </option>
                  <option value="Unpaid">Unpaid</option>
                  <option value="Partially Paid">Partially Paid</option>
                  <option value="Paid">Paid</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="amount_paid"
                >
                  Amount Paid ({invoice ? invoice.amount_paid : ""} KWD)
                </label>
                <input
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step="0.1"
                  placeholder="Amount Paid"
                  onChange={(e) => setAmountPaid(e.target.value)}
                />
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_type"
                >
                  Payment Type ({invoice ? invoice.payment_type : ""})
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  name="payment_type"
                  onChange={(e) => setPaymentType(e.target.value)}
                >
                  <option value="-- Select payment type --">
                    -- Select payment type --
                  </option>
                  <option value="K-Net">K-Net</option>
                  <option value="Cash">Cash</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="customer"
                >
                  Customer (
                  {invoice &&
                    `${invoice.customer_name} ${invoice.customer_surname}`}
                  )
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  id="customer"
                  onChange={(e) => setCustomerName(e.target.value)}
                  value={invoice ? invoice.customer_name : ""}
                >
                  <option label="-- Select a customer --"></option>
                  {customers &&
                    customers.map((customer, index) => (
                      <option
                        key={index}
                        label={`${customer.name} ${customer.surname}`}
                        value={customer.name}
                      ></option>
                    ))}
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Phone Number ({invoice ? invoice.customer_phone : ""})
                </label>
                <input
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="tel"
                  placeholder="Phone Number"
                  onChange={(e) => setPhoneNumber(e.target.value)}
                />
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default ShowInvoice;
