import React, { useEffect, useState } from "react";

const UnitInput = ({ label, name, icon, unitType, onChange, measurement }) => {
  // const [value, setValue] = useState(measurement || ""); // Fallback to empty string
  const [value, setValue] = useState(measurement || ""); // Fallback to empty string

  useEffect(() => {
    // Convert value when unitType changes
    if (measurement !== undefined && measurement !== null) {
      if (unitType === "Inches") {
        setValue((prev) => (prev / 2.54).toFixed(2)); // Convert to inches
      } else {
        setValue((prev) => (prev * 2.54).toFixed(2)); // Convert to centimeters
      }
    }
  }, [unitType, measurement]);

  const handleChange = (e) => {
    const newValue = parseFloat(e.target.value) || "";
    setValue(newValue);
    onChange(name, newValue); // Call the parent's onChange handler
  };

  return (
    <div className="flex flex-col gap-[1rem] border-2 border-[black] my-[1rem]">
      <img src={icon || ""} alt={label} className="w-[12rem] h-[6rem] m-auto" />
      <label className="text-center">
        {label} ({unitType === "Inches" ? "in" : "cm"})
      </label>
      <input
        className="text-center p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg focus:border-[blue]"
        type="number"
        step={0.1}
        name={name}
        placeholder={label}
        value={value || ""} // Ensure value is either a string or a number
        onChange={handleChange}
      />
    </div>
  );
};

export default UnitInput;
