import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";

const Customers = () => {
  const [customers, setCustomers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamco-shop-backend.onrender.com/customers";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchCustomers = async (queryParams = "") => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      setCustomers(response.data.customers);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        toast.error("An error occurred: " + error.message);
      }
      setCustomers([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = `Khamco Shop ${t("Customers")}`;

    fetchCustomers(location.search);
  }, [apiUrl, authKey, location.search, t, currentPage]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    const debouncedSearch = Debounce(searchParams, 500);
    navigate(`?${debouncedSearch}`);
  };

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[4rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/customers/new-customer"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              {t("New Customer")} <i className="fas fa-user-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                placeholder={`${t("Search Customer Name")}`}
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="date"
                name="date"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                name="phone"
                placeholder={`${t("Search Customer Phone Number")}`}
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                name="uniqueId"
                placeholder={`${t("Search Customer ID")}`}
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">{`${t(
            "Customer List"
          )} (${customers && customers.length})`}</h1>
          <div className="pagination-controls">
                  <button
                    disabled={currentPage === 1}
                    onClick={handlePreviousPage}
                  >
                    Previous
                  </button>
                  <span>
                    {currentPage} of {totalPages}
                  </span>
                  <button
                    disabled={currentPage === totalPages}
                    onClick={handleNextPage}
                  >
                    Next
                  </button>
                </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[3rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          {customers &&
            customers.length > 0 &&
            customers.map((customer, index) => (
                <div
                  className="w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-start md:justify-evenly flex-wrap items-center gap-[2rem]"
                  dir="auto"
                  key={index}
                >
                  <p className="capitalize">
                    <strong>{t("Name")}</strong>:{" "}
                    {`${customer.name} ${customer.surname}`}{" "}
                    {customer.membership_status === "VIP" ? (
                      <i className="fas fa-star text-[#d2b929] animate-pulse"></i>
                    ) : (
                      ""
                    )}
                  </p>
                  <p className="capitalize">
                    <strong>{t("Last Measurement")}</strong>:{" "}
                    {customer.measurements.length > 0
                      ? new Date(
                          customer.measurements[0].date_created
                        ).toLocaleDateString("en-us", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        })
                      : "None"}
                  </p>
                  <p className="capitalize">
                    <strong>{t("Mobile")}</strong>:{" "}
                    <a href="tel:<%= customer.phone %>">{customer.phone}</a>
                  </p>
                  <p className="capitalize">
                    <strong>{t("Dependents")}</strong>:{" "}
                    {customer.dependents.length}
                  </p>
                  <Link
                    to={`/customers/${customer._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-[4rem] text-center"
                  >
                    {t("View")}
                  </Link>
                </div>
            ))
            
            }
          {customers.length === 0 && (
            <p className="text-center">No customer data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Customers;

export function Debounce(value, delay) {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}
