import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const Orders = () => {
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/orders"
    : "https://khamcotailor";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();

  const fetchOrders = async (queryParams = "") => {
    try {
      const response = await axios.get(apiUrl, {
        headers: { Authorization: `Bearer ${authKey}${queryParams}` },
      });
      const sortedOrders = response.data.orders.sort(
        (a, b) => new Date(a.date) - new Date(b.date)
      );
      setOrders(sortedOrders);
      setIsLoading(false);
      setDisplay(true);
    } catch (error) {
      setMessage(error.message);
      setIsLoading(false);
      setDisplay(true);
      setOrders([]);
    }
  };

  useEffect(() => {
    document.title = "Orders - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    return () => {
      fetchOrders();
    };
  }, [apiUrl, authKey, userLanguage]);

  const applyAnimation = (orderStatus) => {
    switch (orderStatus) {
      case "In Progress":
        return "order-in-progress";
      case "Complete":
        return "order-complete";
      case "Awaiting Delivery":
        return "order-awaiting";
      case "Delivered":
        return "order-delivered";
      default:
        return "";
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          id="prices"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <div className="utilities">
            <Link to="/orders/new-order">
              New Order <i className="fas fa-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          {isLoading &&
            new Array(3).fill(
              <div className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#888] p-[3rem] shadow-md shadow-[#333] rounded-[0.5rem] animate-pulse"></div>
            )}
          {display && (
            <>
              <h1 className="title">All Orders</h1>
              <div className="utilities">
                <form action="/orders" method="GET" className="search-engine">
                  <input
                    type="text"
                    placeholder="Search Customer Name"
                    name="name"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form action="/orders" method="GET" className="search-engine">
                  <input
                    type="text"
                    placeholder="Search Customer Phone Number"
                    name="phone"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  action="/orders"
                  method="GET"
                  className="search-engine"
                  id="date-form"
                >
                  <input
                    type="date"
                    name="order_date"
                    id="date-search"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-check"></i>
                  </button>
                </form>
                <form action="/orders" method="GET" className="search-engine">
                  <select
                    name="order_status"
                    id="order_status"
                    className="search-input"
                  >
                    <option selected disabled>
                      Search Order Status
                    </option>
                    <option value="Accepted">Accepted</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Complete">Complete</option>
                    <option value="Awaiting Delivery">Awaiting Delivery</option>
                    <option value="Delivered">Delivered</option>
                  </select>
                  <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                  </button>
                </form>
              </div>
              {orders ? (
                orders.map((order, index) => (
                  <div
                    key={index}
                    className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem]"
                    dir={writingDirection}
                  >
                    <p className="capitalize">
                      <strong>Customer Name</strong>:{" "}
                      {`${order.customer_name} ${order.customer_surname}`}
                    </p>
                    {order.order_start_time && (
                      <p className="capitalize">
                        <strong>Order Start Date</strong>:{" "}
                        {new Date(order.order_start_time).toLocaleDateString(
                          "en-us",
                          {
                            month: "numeric",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </p>
                    )}
                    <p className="capitalize">
                      <strong>Order Status</strong>:{" "}
                      <span
                        className={`py-[0.2rem] px-[0.5rem] rounded-[0.7rem] ${applyAnimation(
                          order.order_status
                        )}`}
                      >
                        {order.order_status}
                      </span>
                    </p>
                    <Link to={`/orders/${order._id}`} className="edit-btn">
                      View
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center">No orders available.</p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Orders;
