import React, { useEffect, useTransition } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";
import { useTranslation } from "react-i18next";

const Invoices = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [invoices, setInvoices] = useState([]);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation();

  const getInvoices = async (queryParams = "") => {
    try {
      const response = await axios.get(`${serverUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setIsLoading(false);
      if (response.data.invoices) {
        setInvoices(response.data.invoices);
      } else {
        setInvoices([]);
      }
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Invoices - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    getInvoices(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          id="invoice-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices/new"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              {t("New Invoice")} <i className="fas fa-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="text"
                placeholder="Search Customer Name"
                name="customer_name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="text"
                placeholder="Search Invoice ID"
                name="uniqueId"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="date"
                name="date"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <select
                name="payment_status"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              >
                <option value="">-- Search Payment Status --</option>
                <option value="Unpaid">Unpaid</option>
                <option value="Partially Paid">Partially Paid</option>
                <option value="Paid">Paid</option>
                <option value="Other">Other</option>
              </select>
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <select
                name="payment_type"
                id="payment_type"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              >
                <option value="K-Net">K-Net</option>
                <option value="Cash">Cash</option>
                <option value="Other">Other</option>
              </select>
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="tel"
                name="phone"
                placeholder="Phone number"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">{`Invoices (${
            invoices && invoices.length
          })`}</h1>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                  dir={writingDirection}
                ></div>
              ))}
          {invoices.length > 0 ? (
            invoices.map((invoice, index) => (
              <div
                className="flex w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] my-[1rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>Name</strong>: {invoice.customer_name}{" "}
                  {invoice.customer_surname}
                </p>
                <p className="capitalize">
                  <strong>Last Measurement</strong>:{" "}
                  {new Date(invoice.date).toLocaleDateString("en-us", {
                    month: "long",
                    year: "numeric",
                    day: "numeric",
                  })}
                </p>
                <Link
                  to={`/invoices/${invoice._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 no-underline"
                >
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="empty">No invoice data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Invoices;
