import React, { useState } from "react";

const Clothing = ({ label, imageUrl }) => {
  const [isChecked, setIsChecked] = useState(false);
  return (
    <>
        <input
        type="checkbox"
        id="clothing_item"
        onChange={() => setIsChecked(!isChecked)}
        className="w-[8rem] accent-khamco-primary cursor-pointer"
      />
      <label
        htmlFor="clothing_item"
        onClick={(e) => e.preventDefault()}
        className={`w-28 hover:bg-[#ddd] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 ${
          isChecked ? "border-2 border-khamco-primary bg-[#ccc]" : ""
        }`}
      >
        <img src={imageUrl} alt={label} />
        <p className="text-sm">{label}</p>
      </label>
    </>
  );
};

export default Clothing;
