import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Navbar from "../../../components/Navbar";
import Footer from "../../../components/Footer";
import axios from "axios";

const Measurements = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const [measurements, setMeasurements] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/measurements`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/measurements`;
    const authKey = local

    useEffect(()=>{
        const fetchData = async () => {
            await axios
              .get(apiUrl, {
                headers: { Authorization: `Bearer ${authKey}` },
              })
              .then((response) => {
                setCustomer(response.data.customer);
              })
              .catch((error) => {
                setMessage(error.message);
              });
          };
    }, [])
  return (
    <>
      <Navbar />
      <main>
        <section className="customer-details" dir={writingDirection}>
          <div className="utilities">
            <Link to={`/customers/${customer.id}`}>
              <i className="fas fa-arrow-left"></i> Customer{" "}
              {`${customer.name} ${customer.surname}`}
            </Link>
          </div>
          <h1 className="title">
            Measurements {`${customer.name} ${customer.surname}`}
          </h1>
          <div className="khamco-card">
            <div className="utilities">
              <Link
                to={`/customers/create-measurement/${customer.id}`}
                className="edit-btn"
              >
                New Measurement <i className="fas fa-plus"></i>
              </Link>
            </div>
            {measurements &&
              measurements.map((measurement) => (
                <div className="row">
                  <span>
                    <strong>Date</strong>
                    {new Date(measurement.date_created).toLocaleDateString(
                      "en-us",
                      {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      }
                    )}
                  </span>
                  <span>
                    <strong>Waist</strong>
                    {measurement.waist}
                  </span>
                  <span>
                    <strong>Natural Waist</strong>
                    {measurement.natural_waist} inches.
                  </span>

                  <div
                    className="utilities"
                    style={{
                      borderBottom: "0.2rem solid gray",
                      paddingBottom: "1rem",
                    }}
                  >
                    <Link
                      to={`/customers/${customer.id}/measurements/${measurement.id}`}
                      className="edit-btn"
                    >
                      View
                    </Link>
                  </div>
                </div>
              ))}
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Measurements;
