import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import UserForm from "./UserForm";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const RegisterUser = () => {
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin"
    : "https://khamco-shop-backend.onrender.com/admin";
  const [checkMaster, setCheckMaster] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [language, setLanguage] = useState("en");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    let isMounted = true;

    document.title = "User Registration - Khamco Shop";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/register-user`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setCheckMaster(response.data.checkMaster);
            setAuthorizedUser(response.data.authorizedUser);
          }
        })
        .catch((error) => {
          if (isMounted) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, navigate, serverUrl]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post(
        `${serverUrl}/register`,
        {
          name: firstName,
          surname: lastName,
          email: email,
          role: role,
          language: language,
          password: password,
          password2: confirmPassword,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setCheckMaster(response.data.checkMaster);
      setAuthorizedUser(response.data.authorizedUser);
      navigate(`/admin/users/${response.data.user._id}`);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <AdminPanel />
        <section>
          <h1 className="title">Register User</h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <UserForm
              page="register"
              checkMaster={checkMaster}
              authorizedUser={authorizedUser}
              loading={loading}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setEmail={setEmail}
              setPassword={setPassword}
              setConfirmPassword={setConfirmPassword}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default RegisterUser;
