import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const CustomerDetails = () => {
  const userLanguage = useStoreState((state) => state.userLanguage);
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}`
    : `https://khamcotailor/customers/${id}`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = `Customer Details - ${
      customer ? `${customer._id} -` : ""
    } Khamco Shop`;

    const fetchCustomerData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
        })
        .catch((error) => {
          setMessage(error.message);
        });
    };

    const determineWritingDirection = () => {
      if(userLanguage === "ar" || userLanguage === "ur"){
        setWritingDirection("rtl");
      }
    }
    determineWritingDirection();

    return () => {
      fetchCustomerData();
    };
  }, [apiUrl, authKey, customer, userLanguage]);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
            <Link to="/customers">
              <i className="fas fa-arrow-left"></i> Customers
            </Link>
            <p className="message">{message}</p>
          </div>

          {customer && (
            <div className="khamco-card" dir={writingDirection}>
              <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                <Link
                  to={`/customers/edit/${customer._id}`}
                  className="edit-btn"
                >
                  Edit
                </Link>
                <Link
                  to={`/invoices/add/invoice/${customer._id}`}
                  className="edit-btn"
                  target="_blank"
                >
                  Create Invoice
                </Link>
                <Link
                  to={`/customers/${customer._id}/invoices`}
                  className="edit-btn"
                  target="_blank"
                >
                  All Invoices
                </Link>
                <form
                  action="/customers/<%= customer.id %>?_method=DELETE"
                  method="POST"
                  className="d-inline"
                >
                  <button
                    type="submit"
                    className="delete-btn"
                    title="Delete this customer"
                  >
                    Delete
                  </button>
                </form>
                <Link
                  to={`/customers/create-dependent/${customer._id}`}
                  className="edit-btn"
                >
                  Add Dependent
                </Link>
                <button
                  className="print-btn"
                  title="Print this document"
                  onClick={() => window.print()}
                >
                  Print<i className="fas fa-print"></i>
                </button>
                <form
                  action="/customers/<%= customer.id %>/membership?_method=PUT"
                  method="POST"
                >
                  <button
                    className="print-btn"
                    title="<%=__('Click to change customer membership') %>"
                  >
                    Membership
                    {customer.membership_status === "VIP" && (
                      <i className="fas fa-star blink"></i>
                    )}
                  </button>
                </form>
              </div>
              <h1 className="title">Customer Details</h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">ID</strong>
                  <p>{customer.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Name</strong>
                  <p>
                    {customer.uniqueId}
                    {customer.uniqueId}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">
                    Phone Number
                  </strong>
                  <a href={`tel:${customer.uniqueId}`}>{customer.uniqueId}</a>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">
                    Last Measurement
                  </strong>
                  <p>
                    {new Date().toLocaleDateString("en-us", {
                      month: "long",
                      year: "numeric",
                      day: "numeric",
                    })}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Address</strong>
                  <p>{customer.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Email</strong>{" "}
                  <a href="mailto:<%=customer.email%>">{customer.uniqueId}</a>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Body Type</strong>
                  <p>{customer.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Status</strong>
                  {customer.uniqueId}
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Membership</strong>
                  {customer.membership_status === "VIP" ? (
                    <p className="blink">
                      {customer.membership_status}{" "}
                      <i className="fas fa-star"></i>
                    </p>
                  ) : (
                    <p>{customer.membership_status}</p>
                  )}
                </span>
              </div>
              <div className="flex flex-col gap-[1rem]">
                <h2 className="title">Military Details</h2>
              </div>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">ID Number</strong>
                  <p>{customer.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Rank</strong>
                  <p>{customer.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Department</strong>
                  <p>{customer.uniqueId}</p>
                </span>
              </div>
              <div className="flex flex-col gap-[1rem]">
                <h2 className="title">Measurements</h2>
              </div>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">Height</strong>
                  <p>{customer.uniqueId} inches</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                  <strong className="text-khamco-secondary">
                    Other Details
                  </strong>
                  <p>{customer.uniqueId}</p>
                </span>
              </div>
              <div className="flex justify-center gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}/measurements`}
                  className="edit-btn"
                >
                  Previous Measurements {customer.measurements.length}
                </Link>
              </div>
              <div className="flex flex-col gap-[1rem]">
                <h2 className="title">Dependents Details</h2>
              </div>
              <div className="flex flex-col gap-[1rem]">
                {customer.dependents.length > 0 ? (
                  customer.dependents.map((dependent) => (
                    <span
                    className="inline-flex justify-start gap-[2rem] text-balance flex-wrap border-b-[gray] border-b-[0.2rem] pb-[1rem]"
                    >
                      <strong className="text-khamco-secondary">Name</strong>
                      <p>
                        {customer.uniqueId}
                        {customer.uniqueId}
                      </p>
                      <Link to={`/customers/${customer._id}/dependents/${dependent.id}`} className="edit-btn">
                        View
                      </Link>
                    </span>
                  ))
                ) : (
                  <p className="empty">This customer has no dependents.</p>
                )}
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CustomerDetails;
