import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import AdminPanel from "../../components/AdminPanel";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const AdminRevenue = () => {
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/revenue"
    : "https://khamco-shop-backend.onrender.com/admin/revenue";
  const [financeData, setFinanceData] = useState([]);
  const [dailyRevenue, setDailyRevenue] = useState([]);
  const [weeklyRevenue, setWeeklyRevenue] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [yearlyRevenue, setYearlyRevenue] = useState([]);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Recent Activity");
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Revenue Data - Khamco Shop";

    const fetchData = async () => {
      await axios
        .get(serverUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setFinanceData(response.data.finances);
          setDailyRevenue(response.data.daily_revenue);
          setWeeklyRevenue(response.data.weekly_revenue);
          setMonthlyRevenue(response.data.monthly_revenue);
          setYearlyRevenue(response.data.yearly_revenue);
          setProducts(response.data.products);
          setServices(response.data.services);
          setIsLoading(false);
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(true);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, serverUrl]);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="admin-dashboard font-arial">
        {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Annual Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  `01/01/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                KWD {yearlyRevenue.length ? yearlyRevenue[0].totalAmount : "--"}
              </h2>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Monthly Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  `${new Date().getMonth() + 1}/01/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                KWD {monthlyRevenue.length ? monthlyRevenue[0].totalAmount : "--"}
              </h2>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Weekly Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  new Date() - 7 * 24 * 60 * 60 * 1000
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                KWD {weeklyRevenue.length ? weeklyRevenue[0].totalAmount : "--"}
              </h2>
            </div>
            <div className="metric">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Daily Revenue
              </h2>
              <p>
                from{" "}
                {`${new Date(
                  `${
                    new Date().getMonth() + 1
                  }/${new Date().getDate()}/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })} at 12:00 AM`}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                KWD {dailyRevenue.length ? dailyRevenue[0].totalAmount : "--"}
              </h2>
            </div>
          </div>
          <div className="search-engine mt-[4rem]">
            <select
              className="search-input"
              onChange={(e) => setSelectedIndex(e.target.value)}
            >
              <option>-- Select Report --</option>
              <option>Recent Activity</option>
              <option>Profit Margins</option>
            </select>
          </div>
          {selectedIndex === "Recent Activity" && (
            <table>
              <caption>Recent Activity</caption>
              <thead>
                <tr>
                  <td>Amount Paid</td>
                  <td>Payment Type</td>
                  <td>Customer</td>
                  <td>Date</td>
                </tr>
              </thead>
              <tbody>
                {financeData &&
                  financeData.map((finance, index) => (
                    <tr key={index}>
                      <td>KWD {finance.amount_paid}</td>
                      <td>{finance.payment_type}</td>
                      <td>
                        {finance.customer_name} {finance.customer_surname}
                      </td>
                      <td>
                        {new Date(finance.date).toLocaleDateString("en-US", {
                          month: "short",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          {selectedIndex === "Profit Margins" && (
            <table>
              <a
                className="edit-btn"
                id="allProductsBtn"
                href="/admin/products"
                target="_blank"
              >
                Product Costs <i className="fas fa-dollar-sign"></i>
              </a>
              <caption>Profit Margins</caption>
              <thead>
                <tr>
                  <td>Product Name</td>
                  <td>Price</td>
                  <td>Cost</td>
                  <td>Margin</td>
                </tr>
              </thead>
              <tbody>
                {products &&
                  products.map((product, index) => (
                    <tr key={index}>
                      <td>{product.name}</td>
                      <td>KWD {product.price}</td>
                      <td>KWD {product.cost}</td>
                      <td
                        className={`${
                          product.margin > 0 ? "text-green-500" : "text-red-500"
                        } font-bold`}
                      >
                        {product.margin}
                      </td>
                    </tr>
                  ))}
              </tbody>
              <thead>
                <tr>
                  <td>Service Name</td>
                  <td>Price</td>
                  <td></td>
                  <td>Margin</td>
                </tr>
              </thead>
              <tbody>
                {services &&
                  services.map((service, index) => (
                    <tr key={index}>
                      <td>{service.service}</td>
                      <td>KWD {service.price}</td>
                      <td></td>
                      <td className="text-green-500 font-bold">100%</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminRevenue;
