import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import OrderForm from "./OrderForm";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";
import CheckBox from "../../components/CheckBox";

const NewOrder = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [loadingData, setLoadingData] = useState(true);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [orderDetails, setOrderDetails] = useState([]);
  const [dependentList, setDependentList] = useState([]);
  const [customerId, setCustomerId] = useState("");
  const [customerDependents, setCustomerDependents] = useState([]);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [tailor, setTailor] = useState("");
  const [driver, setDriver] = useState("");
  const [orderStatus, setOrderStatus] = useState("");
  const [orderNotes, setOrderNotes] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const userLanguage = useStoreState((state) => state.userLanguage);

  useEffect(() => {
    let isMounted = true;

    document.title = "New Order - Khamco Shop";

    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/create/new-order`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setLoadingData(false);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
      } catch (error) {
        if (isMounted) {
          setLoadingData(false);
          toast.error(error.response?.data?.message || error.message);
          console.error(error.message);
        }
      }
    };

    fetchOrderDetails();

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, userLanguage]);

  const handleChange = async (e) => {
    const selectedOption = e.target.options[e.target.selectedIndex];

    if (e.target.value === "-- Select customer --") {
      setCustomerDependents([]);
      setCustomerId(null);
      return;
    }

    if (selectedOption) {
      setCustomerId(
        (e.target.value.split(" ")[0] = customers.find(
          (customer) => customer._id === e.target.value
        )).uniqueId
      );
      setCustomerDependents(
        (e.target.value.split(" ")[0] = customers.find(
          (customer) => customer._id === e.target.value
        )).dependents
      );
    }
  };

  const addQuantity = (name, quantity) => {
    setOrderDetails((prevArray) => {
      // Check if item already exists
      const exists = prevArray.some((item) => item.name === name);
      if (exists) {
        return [...prevArray, { quantity: quantity }];
      }

      return prevArray;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log({ orderDetails: orderDetails });
    // if (!orderStatus) {
    //   toast.warn("The order status is required.");
    //   return;
    // }

    // if (!customerId) {
    //   toast.warn("Please select a customer");
    //   return;
    // }

    // if (!orderDetails) {
    //   toast.warn("Please select a product or service from the list");
    //   return;
    // }
    // setLoading(true);

    // try {
    //   const response = await axios.post(
    //     `${apiUrl}/orders`,
    //     {
    //       order_details: orderDetails,
    //       dependents: dependentList,
    //       order_start_time: new Date(),
    //       customer_id: customerId,
    //       measurer: measurer,
    //       cutter: cutter,
    //       tailor: tailor,
    //       driver: driver,
    //       order_status: orderStatus,
    //       order_notes: orderNotes,
    //     },
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );
    //   toast.success(response.data.message);
    //   navigate(`/orders/${response.data.order._id}`);
    // } catch (error) {
    //   toast.error(error.response?.data?.message || error.message);
    //   setLoading(false);
    // }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center relative"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> Orders
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">New Order</h1>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <h1 className="text-center my-[1rem] text-xl font-bold">
              Product List
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading products...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-center flex-wrap">
              {products &&
                products.map((product, index) => (
                  <>
                    <CheckBox
                      key={index}
                      label={product.name}
                      setOptions={setOrderDetails}
                    />
                  </>
                ))}
            </div>
            <h1 className="text-center my-[1rem] text-xl font-bold">
              Service List
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading services...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-center flex-wrap">
              {services &&
                services.map((service, index) => (
                  <CheckBox
                    key={index}
                    label={service.service}
                    setOptions={setOrderDetails}
                  />
                ))}
              {orderDetails.length > 0 && (
                <p className="text-center my-[1rem] font-bold text-lg text-[red]">
                  Enter quantities below
                </p>
              )}
            </div>
            <div className="w-full flex flex-col mx-auto my-4 relative">
              {orderDetails &&
                orderDetails.map((item, index) => (
                  <input
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    type="number"
                    step={0.1}
                    key={index}
                    placeholder={`Enter Quantity for ${item.name}`}
                    onChange={(e) => addQuantity(item.name, e.target.value)}
                  />
                ))}
            </div>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              Customer Name
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              onChange={handleChange}
            >
              <option
                label="-- Select customer --"
                value="-- Select customer --"
              ></option>
              {customers &&
                customers.map((customer, index) => (
                  <option
                    key={index}
                    value={customer._id}
                    label={`${customer.name} ${customer.surname}`}
                  ></option>
                ))}
            </select>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            {customerDependents.length > 0 && (
              <h1 className="text-center my-[1rem] text-xl font-bold">
                Dependents
              </h1>
            )}
            <div className="flex gap-[1rem] w-full justify-start flex-wrap">
              {customerDependents.length > 0 &&
                customerDependents.map((dependent, index) => (
                  <CheckBox
                    key={index}
                    label={dependent.name}
                    setOptions={setDependentList}
                  />
                ))}
              {!customerId && <p>Select a customer above</p>}
              {customerId && customerDependents.length <= 0 && (
                <p>Customer has no dependents...</p>
              )}
            </div>
          </div>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <OrderForm
              customerId={customerId}
              loading={loading}
              setMeasurer={setMeasurer}
              setCutter={setCutter}
              setTailor={setTailor}
              setDriver={setDriver}
              setOrderStatus={setOrderStatus}
              setOrderNotes={setOrderNotes}
            />
          </form>
          {orderDetails.length > 0 && (
            <button className="bg-khamco-primary py-1 px-[0.5rem] min-w-10 fixed top-[50%] right-[10%] rounded-full text-[1.5rem] text-white font-bold border-2 border-khamco-secondary">
              {orderDetails.length || 0}
            </button>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewOrder;
