import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const AdminTailorDetails = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const apiUrl = `http://localhost:5000/admin/users/${id}`;
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    document.title = `Admin Tailor Details - ${user ? `$user.name` : ""} Khamco Shop`
    const getUserDetails = async () => {
      await axios
        .get(apiUrl)
        .then((response) => {
          setUser(response.data.user);

          if (response.data.user) {
            setIsLoading(false);
            setDisplay(true);
          }
        })
        .catch((error) => {
          setIsLoading(true);
          setDisplay(false);
          console.error(error);
          setMessage(error.message);
        });
    };

    return () => {
      getUserDetails();
    };
  }, [apiUrl]);
  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          <p className="message">{message}</p>
          {isLoading && (
            <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="h-[100vh] w-2/4 m-auto rounded-[2rem] bg-[#ccc] animate-pulse"></div>
            </div>
          )}
          {display && user && (
            <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="khamco-card w-full">
                <div className="utilities">
                  <Link to={`/admin/edit-user/${user.id}`} className="edit-btn">
                    Edit
                  </Link>
                  <form
                    action="/admin/<%= user.id %>?_method=DELETE"
                    method="POST"
                    className="d-inline"
                  >
                    <button type="submit" className="delete-btn">
                      Delete
                    </button>
                  </form>
                </div>
                <h1 className="title">User Details {user.name}</h1>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">ID</strong>
                    <p className="capitalize">{user.id}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Name</strong>
                    <p className="capitalize">{user.name}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Surname</strong>
                    <p className="capitalize">{user.surname}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Role</strong>
                    <p className="capitalize">{user.role}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Language</strong>
                    <p className="capitalize">
                      {user.language === "en" && "English"}
                      {user.language === "ur" && "Urdu"}
                      {user.language === "ar" && "Arabic"}
                      {user.language === "hi" && "Hindi"}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Date Created</strong>
                    <p className="capitalize">
                      {new Date(user.date).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })}
                    </p>
                  </span>
                </div>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
