import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";

const View = () => {
  const {id} = useParams();
  const [message, setMessage] = useState("");
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [product, setProduct] = useState({}); 
  useEffect(() => {
    document.title = `Product Details - ${product.id} Khamco Shop Home`;
  }, [product.id]);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section dir={writingDirection}>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
            <p className="message">{message}</p>
          </div>
          <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
            <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
              <Link to={`/products/edit/${product.id}`} className="edit-btn">
                Edit
              </Link>
              <form
                action="/products/<%= product.id %>?_method=DELETE"
                method="POST"
                className="d-inline"
              >
                <button type="submit" className="delete-btn">
                  Delete
                </button>
              </form>
            </div>
            <h1 className="title">{product.name}</h1>
            <img
              src={product.photoPath}
              alt={product.name}
              className="w-[20vw] h-[40vh] m-auto"
            />
            <div className="flex flex-col gap-[1rem]">
              <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                <strong>ID</strong>
                <p>{product.id}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                <strong>Quantity</strong>
                <p>{product.quantity} meters</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                <strong>Price</strong>
                <p>KWD {product.price}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                <strong>Category</strong>
                <p>{product.category}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                <strong>Date</strong>
                <p className="capitalize">
                  {product.date.toLocaleDateString("en-us", {
                    month: "long",
                    year: "numeric",
                    day: "numeric",
                  })}
                </p>
              </span>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default View;
