import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";
import { toast } from "react-toastify";
import { applyAnimation, hasRoles } from "../../utilities";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();
  const user = useStoreState((state) => state.user);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalOrders, setTotalOrders] = useState(0);

  const fetchOrders = async (queryParams = "") => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${apiUrl}/orders${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setOrders(response.data.orders);
      setTotalOrders(response.data.totalOrders);setTotalPages(response.data.totalPages);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    } finally {
      setIsLoading(false);
      setDisplay(true);
      setOrders([]);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Orders - Khamco Shop";
    const searchParams = new URLSearchParams(location.search);
    searchParams.set("page", currentPage); // Add page param to the query
    fetchOrders(`?${searchParams.toString()}`);
    fetchOrders(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form);
    searchParams.set("page", currentPage); // Ensure current page is included in search
    navigate(`?${searchParams.toString()}`);
  };

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]">
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#888] p-[3rem] shadow-md shadow-[#333] rounded-[0.5rem] animate-pulse"
                ></div>
              ))}
          {display && (
            <>
              <h1 className="text-center text-[2rem] font-minimoBold my-[2rem]">
                All Orders ({orders.length || 0})
              </h1>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="text"
                    placeholder="Search Customer ID"
                    name="customer_id"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="text"
                    placeholder="Search Customer Phone Number"
                    name="customer_phone"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <input
                    type="date"
                    name="order_date"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  />
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-check"></i>
                  </button>
                </form>
                <form
                  className="w-full md:w-2/5 flex justify-between mb-8"
                  onSubmit={handleSearch}
                >
                  <select
                    name="order_status"
                    className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  >
                    <option value="">-- Search Order Status --</option>
                    <option value="Accepted">Accepted</option>
                    <option value="In Progress">In Progress</option>
                    <option value="Complete">Complete</option>
                    <option value="Awaiting Delivery">Awaiting Delivery</option>
                    <option value="Delivered">Delivered</option>
                  </select>
                  <button
                    type="submit"
                    className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
                  >
                    <i className="fas fa-search"></i>
                  </button>
                </form>
              </div>
              {orders && orders.length > 0 ? (
                orders.map((order, index) => (
                  <div
                    key={index}
                    className="flex justify-evenly gap-[2rem] items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem]"
                  >
                    <p className="capitalize">
                      <strong>Customer ID</strong>: {order.customer_id}
                    </p>
                    <p className="capitalize">
                      <strong>Order Date</strong>:{" "}
                      <span>
                        {new Date(order.order_date).toLocaleDateString(
                          "en-us",
                          {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </span>
                    </p>
                    <p className="capitalize">
                      <strong>Order Status</strong>:{" "}
                      <span
                        className={`py-[0.2rem] px-[0.5rem] rounded-[0.7rem] ${applyAnimation(
                          order.order_status
                        )}`}
                      >
                        {order.order_status}
                      </span>
                    </p>
                    <Link
                      to={`/orders/${order._id}`}
                      className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-[5rem] text-center"
                    >
                      View
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center">No orders available.</p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Orders;
