import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useStoreState } from "easy-peasy";
import { toast } from "react-toastify";

const Customers = () => {
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customers, setCustomers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamco-shop-backend.onrender.com/customers";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();

  const fetchCustomers = async (queryParams = "") => {
    await axios
      .get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setCustomers(response.data.customers);
        setIsLoading(false);
      })
      .catch((error) => {
        setIsLoading(false);
        setCustomers([]);
        toast.error(error.response?.data?.message || error.message);
      });
  };

  useEffect(() => {
    document.title = "Khamco Shop Customers";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchCustomers(location.search);
  }, [apiUrl, authKey, location.search, userLanguage, writingDirection]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[4rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link to="/customers/new-customer" className="text-[blue] no-underline hover:underline cursor-pointer">
              New Customer <i className="fas fa-user-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input
                type="text"
                placeholder="Search Customer First Name"
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input type="date" name="date" className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary" />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input
                type="text"
                name="phone"
                placeholder="Search Customer Phone Number"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input
                type="text"
                name="uniqueId"
                placeholder="Search Customer ID"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">{`Customer List (${
            customers && customers.length
          })`}</h1>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[3rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                  dir={writingDirection}
                ></div>
              ))}

          {customers && customers.length > 0 ? (
            customers.map((customer, index) => (
              <div
                className="w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-start md:justify-evenly flex-wrap items-center gap-[2rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>Name</strong>:{" "}
                  {`${customer.name} ${customer.surname}`}{" "}
                  {customer.membership_status === "VIP" ? (
                    <i className="fas fa-star blink"></i>
                  ) : (
                    ""
                  )}
                </p>
                <p className="capitalize">
                  <strong>Last Measurement</strong>:{" "}
                  {customer.measurements.length > 0
                    ? new Date(
                        customer.measurements[0].date_created
                      ).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })
                    : "None"}
                </p>
                <p className="capitalize">
                  <strong>Mobile</strong>:{" "}
                  <a href="tel:<%= customer.phone %>">{customer.phone}</a>
                </p>
                <p className="capitalize">
                  <strong>Dependents</strong>: {customer.dependents.length}
                </p>
                <Link to={`/customers/${customer._id}`} className="edit-btn">
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="text-center">No customer data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Customers;
